/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        TableHelper utility function
 * CVM-Role:        Utility
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     Transforms an AST to a grid table.
 *                  Cf. https://pandoc.org/MANUAL.html#tables
 *
 * END HEADER
 */

import calculateColSizes from './calculate-col-sizes'
import type { ColAlignment } from './types'

/**
 * Takes a set of AST columns and returns the number of lines they will span if
 * built as a string table
 *
 * @param   {string[]}  columns  The column contents
 *
 * @return  {number}             The amount of raw Markdown lines required to render
 */
function calcRawCellRows (columns: string[]): number {
  return Math.max(...columns.map(col => col.split('\n').length))
}

/**
 * Builds a header row for a grid table, based on the column sizes and the
 * alignment. As a symbol, pass in '=' if the grid table has a header row, or
 * '-' if it does not, and place this row accordingly.
 *
 * @param   {number[]}        colSizes      The column sizes of the table
 * @param   {ColAlignment[]}  colAlignment  The alignment specs
 * @param   {'='|'-'}         symbol        The symbol (depends on header row)
 *
 * @return  {string}                        The built grid header row
 */
function buildGridHeaderRow (colSizes: number[], colAlignment: ColAlignment[], symbol: '='|'-'): string {
  let headerRow = '+'
  for (let col = 0; col < colSizes.length; col++) {
    // Respect the spaces left and right and account for alignment
    switch (colAlignment[col]) {
      case 'left':
        headerRow += symbol.repeat(colSizes[col] + 2) + '+'
        break
      case 'center':
        headerRow += ':' + symbol.repeat(colSizes[col]) + ':+'
        break
      case 'right':
        headerRow += symbol.repeat(colSizes[col] + 1) + ':+'
        break
    }
  }
  return headerRow
}

export default function buildGridTable (ast: string[][], colAlignment: ColAlignment[], hasHeader: boolean = true): string {
  // Setup: colSizes is the maximum length of any cell inside the given column
  const colSizes = calculateColSizes(ast)
  const numRows = ast.length
  const numCols = colSizes.length

  // This is a template separator row, added in between each row
  const sep = '+' + colSizes.map(elem => '-'.repeat(elem + 2)).join('+') + '+'
  const header = buildGridHeaderRow(colSizes, colAlignment, hasHeader ? '=' : '-')

  // Begin the table with a separator row
  const rows: string[] = []
  if (hasHeader) {
    rows.push(sep)
  } else {
    // Custom header row (similar to below, but with '-' instead of '=')
    rows.push(header)
  }

  // Now iterate over every row in the table
  for (let row = 0; row < numRows; row++) {
    // A single row in the AST can span multiple actual rows, delimited by \n
    const actualRows = calcRawCellRows(ast[row])

    // This is an inner table used to prepare the columns inside this row for
    // stringification. We need to pre-fill it as we have to pivot the cells
    // from col/row to row/col ordering.
    const innerAST: string[][] = []
    for (let i = 0; i < actualRows; i++) {
      const innerRow = []
      for (let j = 0; j < numCols; j++) {
        innerRow.push('') // Arbitrary, will be overwritten below
      }
      innerAST.push(innerRow)
    }

    // After the preparation, iterate over the columns inside the row
    for (let col = 0; col < numCols; col++) {
      // Split into the individual "actual" rows of the cell, and pad to the
      // full length
      let splitCell = ast[row][col].split('\n')
      while (splitCell.length < actualRows) {
        splitCell.push('')
      }

      // Now, pad each "split" cell accordingly
      splitCell = splitCell.map(c => ' ' + c.padEnd(colSizes[col]) + ' ')

      // Now add all parts of the given cell to the inner table. Here, we pivot
      // the data from [col][row] to [row][col]
      for (let i = 0; i < actualRows; i++) {
        innerAST[i][col] = splitCell[i]
      }
    }

    // At this point we have one or more actual rows we have to build. The
    // contents are already correctly padded and stored as [row][col] arrays
    for (const rawRow of innerAST) {
      rows.push('|' + rawRow.join('|') + '|')
    }

    // After the first (logical) row we need a header row, which we build here.
    if (row === 0 && hasHeader) {
      rows.push(header)
    } else {
      // All other lines are followed by a separator.
      rows.push(sep)
    }
  }

  return rows.join('\n')
}

/**
* @ignore
* BEGIN HEADER
*
* Contains:        Utility function
* CVM-Role:        Utility
* Maintainer:      Hendrik Erz
* License:         GNU GPL v3
*
* Description:     This function takes a table AST and calculates, for each
*                  column, the maximum amount of characters.
*
* END HEADER
*/

/**
 * Calculates the maximum size (characters) of each column of a table AST
 *
 * @param   {string[][]}  ast  The AST
 *
 * @return  {number[]}       The maximum sizes for all columns
 */
export default function calculateColSizes (ast: string[][]): number[] {
  const sizes = []
  for (let col = 0; col < ast[0].length; col++) {
    let colSize = 0
    for (let row = 0; row < ast.length; row++) {
      const cell = ast[row][col]
      let cellLength = cell.length
      if (cell.includes('\n')) {
        // Multi-line cell -> take the longest of the containing rows
        cellLength = Math.max(...cell.split('\n').map(x => x.length))
      }

      if (cellLength > colSize) {
        colSize = cellLength
      }
    }
    sizes.push(colSize)
  }
  return sizes
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        TableHelper utility function
 * CVM-Role:        Utility
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     This parser transforms pipe tables as specified
 *                  in the Pandoc manual into an AST and returns both
 *                  that and the column alignments.
 *                  Cf. https://pandoc.org/MANUAL.html#tables
 *
 * END HEADER
 */

import calculateColSizes from './calculate-col-sizes'
import type { ColAlignment } from './types'

export default function buildPipeTable (ast: string[][], colAlignment: ColAlignment[]): string {
  if (ast.length < 2) {
    throw new Error('Cannot build pipe table: Must have at least two rows.')
  }

  // First, calculate the column sizes
  const colSizes = calculateColSizes(ast)

  // Then, build the table in a quick MapReduce fashion
  const rows = ast.map(row => {
    const rowContents = row.map((col, idx) => {
      if (colAlignment[idx] === 'right') {
        return col.padStart(colSizes[idx], ' ')
      } else {
        return col.padEnd(colSizes[idx], ' ')
      }
    }).join(' | ')
    return `| ${rowContents} |`
  })

  // Finally, insert the required header row at index 2
  const headerRowContents = colSizes.map((size, idx) => {
    if (colAlignment[idx] === 'left') {
      return '-'.repeat(size + 2)
    } else if (colAlignment[idx] === 'center') {
      return ':' + '-'.repeat(size) + ':'
    } else {
      return '-'.repeat(size + 1) + ':'
    }
  }).join('|')

  const headerRow = `|${headerRowContents}|`
  rows.splice(1, 0, headerRow)

  return rows.join('\n')
}

/**
 * BEGIN HEADER
 *
 * Contains:        Utility function
 * CVM-Role:        <none>
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     This function compiles a string of search terms.
 *
 *                  Supported operators:
 *                  * AND (space)
 *                  * OR (pipe, |)
 *                  * NOT (exclamation mark, !)
 *
 * END HEADER
 */

import type { SearchTerm } from '@dts/common/search'

export default function compileSearchTerms (term: string): SearchTerm[] {
  // First sanitize the terms
  const myTerms: SearchTerm[] = []
  let curWord = ''
  let hasExact = false
  let operator: 'AND'|'OR'|'NOT' = 'AND'

  for (let i = 0; i < term.length; i++) {
    const c = term.charAt(i)
    if (c === ' ' && !hasExact) {
      // Spaces mark the end of one search term (except we're in an exact match)
      if (curWord.trim() !== '') {
        myTerms.push({ words: [curWord.trim()], operator })
        curWord = ''
        operator = 'AND' // Reset the operator
      }
      continue
    } else if (c === '|') {
      // We got an OR operator
      // If the next character is a space, we can use a shortcut here
      if (term.charAt(i + 1) === ' ') {
        ++i
      }

      // We know additionally know that the previous operator was an or. But
      // let's check that the user hasn't accidentally deleted one OR-word and
      // now their current search STARTS with a pipe character. If not, we will
      // disregard this OR character and treat what's coming as an AND
      if (myTerms.length > 0) {
        myTerms[myTerms.length - 1].operator = 'OR'
        operator = 'OR'
      }
      continue
    } else if (c === '"') {
      if (!hasExact) {
        // Begin an exact phrase
        hasExact = true
      } else {
        hasExact = false
        // Do not trim the word to account for trailing and
        // ending whitespace within an exact capturing group
        myTerms.push({ words: [curWord], operator })
        curWord = ''
        operator = 'AND'
      }
      continue
    } else if (c === '!' && !hasExact && curWord === '') {
      // An exclamation mark only has meaning if it is preceeded
      // by a space and not within an exact match. Preceeded by
      // space is indicated by an empty curWord variable.
      operator = 'NOT'
      continue // We don't want the ! in the term
    }

    curWord += term.charAt(i)
  }

  // Now that we're through the search terms, clean up

  // If there is a last word (in most cases it should be), add it to the list
  if (curWord.trim() !== '') {
    myTerms.push({ words: [curWord.trim()], operator })
  }

  // Now pack together all consecutive ORs
  // to make it easier for the search in the main process
  let currentOr: SearchTerm = {
    operator: 'OR',
    words: []
  }

  const newTerms: SearchTerm[] = []

  for (let i = 0; i < myTerms.length; i++) {
    if (myTerms[i].operator !== 'OR') {
      if (currentOr.words.length > 0) {
        // Duplicate object so that the words are retained
        newTerms.push(currentOr)
        currentOr = { operator: 'OR', words: [] }
      }
      newTerms.push(myTerms[i])
    } else {
      currentOr.words = currentOr.words.concat(myTerms[i].words)
    }
  }

  // Now push the currentOr if not empty
  if (currentOr.words.length > 0) {
    newTerms.push(currentOr)
  }

  return newTerms
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        Markdown Utilities
 * CVM-Role:        Utility Functions
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     This is the main entry file for various Markdown utilities
 *                  needed throughout the app.
 *
 * END HEADER
 */

import markdownParser, { type MarkdownParserConfig } from '@common/modules/markdown-editor/parser/markdown-parser'
import { parseNode, type ASTNode, type ASTNodeType, type TextNode } from './markdown-ast'
import { type Tree } from '@lezer/common'

export { md2html } from './markdown-to-html'

/**
 * Converts a Markdown string into an AST, utilizing the CodeMirror Markdown
 * parser for the heavy lifting. This effectively takes a Lezer tree and
 * converts it to a custom Markdown AST implementation.
 *
 * @param   {string}   markdown  The Markdown source
 * @param   {Tree}     tree      Optional: If you already have a parsed tree,
 *                               you can pass it here to save some time re-
 *                               parsing the source. Please NOTE: If you pass in
 *                               a tree, you MUST pass the full Markdown source
 *                               that has been used to generate the parse tree,
 *                               since otherwise the offsets in the parse tree
 *                               will be wrong!
 *
 * @return  {ASTNode}            The root node of the AST
 */
export function markdownToAST (markdown: string, tree?: Tree, parserConfig?: MarkdownParserConfig): ASTNode {
  if (tree === undefined) {
    const { parser } = markdownParser(parserConfig).language
    tree = parser.parse(markdown)
  }
  const ast = parseNode(tree.topNode, markdown)
  return ast
}

/**
 * Extracts arbitrary AST nodes. Providing a NodeType that is not used within
 * the AST will return an empty array.
 *
 * @param   {ASTNode}      ast       The AST to extract nodes from
 * @param   {ASTNodeType}  nodeType  The Node type to query
 * @param   {Function}     filter    An optional filter. Receives a node and
 *                                   returns a boolean indicating if the node
 *                                   should be visited.
 *
 * @return  {ASTNode[]}              An array of all found nodes
 */
export function extractASTNodes (ast: ASTNode, nodeType: ASTNodeType, filter?: (node: ASTNode) => boolean): ASTNode[] {
  if (filter !== undefined && !filter(ast)) {
    return []
  }

  if (ast.type === nodeType) {
    return [ast]
  } else if (ast.type === 'FootnoteRef' || ast.type === 'Highlight' || ast.type === 'ListItem' || ast.type === 'Generic' || ast.type === 'Emphasis') {
    let returnNodes: ASTNode[] = []
    for (const child of ast.children) {
      returnNodes = returnNodes.concat(extractASTNodes(child, nodeType, filter))
    }
    return returnNodes
  } else if (ast.type === 'OrderedList' || ast.type === 'BulletList') {
    let returnNodes: ASTNode[] = []
    for (const item of ast.items) {
      returnNodes = returnNodes.concat(extractASTNodes(item, nodeType, filter))
    }
    return returnNodes
  } else if (ast.type === 'Table') {
    let returnNodes: ASTNode[] = []
    for (const row of ast.rows) {
      for (const cell of row.cells) {
        returnNodes = returnNodes.concat(extractASTNodes(cell, nodeType, filter))
      }
    }
    return returnNodes
  } else {
    return []
  }
}

/**
 * Extracts all text nodes from a Markdown AST. Can be used for spellchecking,
 * for example. An optional filter can be provided to exclude text nodes from
 * those nodes that should not be extracted.
 *
 * @param   {ASTNode[]}   ast     The AST root.
 * @param   {Function}    filter  A function that receives an ASTNode and should
 *                                return false if the node should be excluded.
 *
 * @return  {TextNode[]}          A list of all text nodes
 */
export function extractTextnodes (ast: ASTNode, filter?: (node: ASTNode) => boolean): TextNode[] {
  if (filter !== undefined && !filter(ast)) {
    return []
  }

  let textNodes: TextNode[] = []
  if (ast.type === 'Text') {
    textNodes.push(ast)
  } else if (ast.type === 'Heading') {
    textNodes.push(ast.value)
  } else if (ast.type === 'FootnoteRef' || ast.type === 'Highlight' || ast.type === 'ListItem') {
    for (const child of ast.children) {
      textNodes = textNodes.concat(extractTextnodes(child, filter))
    }
  } else if (ast.type === 'Image' || ast.type === 'Link') {
    textNodes.push(ast.alt)
    if (ast.title !== undefined) {
      textNodes.push(ast.title)
    }
  } else if (ast.type === 'OrderedList' || ast.type === 'BulletList') {
    for (const item of ast.items) {
      textNodes = textNodes.concat(extractTextnodes(item, filter))
    }
  } else if (ast.type === 'Generic' || ast.type === 'Emphasis') {
    for (const child of ast.children) {
      textNodes = textNodes.concat(extractTextnodes(child, filter))
    }
  } else if (ast.type === 'Table') {
    for (const row of ast.rows) {
      for (const cell of row.cells) {
        textNodes = textNodes.concat(extractTextnodes(cell, filter))
      }
    }
  }
  return textNodes
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        Citation Parser
 * CVM-Role:        InlineParser
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     This inline parser adds citation elements to the Lezer tree.
 *
 * END HEADER
 */

import { type InlineParser } from '@lezer/markdown'
import extractCitations from '@common/util/extract-citations'

// TODO: Docs for this: https://github.com/lezer-parser/markdown#user-content-blockparser
export const citationParser: InlineParser = {
  // This parser should only match citations
  name: 'citations',
  before: 'Link', // [@citekey, p. 123] will otherwise be detected as a link
  parse: (ctx, next, pos) => {
    // The next char must be either an opening bracket or an @
    if (next !== 64 && next !== 91) {
      // 64 = [; 91 = @
      return -1
    }

    // Ignore anything before pos
    const slice = ctx.text.slice(pos - ctx.offset)

    // Ensure the character before is valid
    const charBefore = pos > 0 ? ctx.slice(pos - 1, pos) : ''
    // NOTE: The Markdown mode will actually count a single inline context with
    // single linebreaks in between (due to hard-wrapping), so we also have to
    // check for newlines.
    const validBefore = charBefore === '' || '( \n\r\t'.includes(charBefore)
    if (!validBefore) {
      return -1
    }

    // NOTE: We MUST always extract the citations from the whole line. Otherwise
    // @-symbols in illegal positions (e.g. emails) will be detected as valid.
    // However, running extractCitations on a considerable piece of text has a
    // large performance impact, so we need to ensure we only run it if there's
    // a reasonable chance there's a citation at this position.
    const citations = extractCitations(slice).filter(c => c.from === 0)

    // The first found citation after currentOffset must be exactly there.
    if (citations.length !== 1) {
      return -1
    }

    // At this point we have a citation and it's at the current pos
    return ctx.addElement(ctx.elt('Citation', pos, pos + citations[0].source.length))
  }
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        extractCitations function
 * CVM-Role:        Utility Function
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     This function can be used to extract citations from a piece
 *                  of Markdown text as closely to the implementation of Pandoc
 *                  Citeproc as possible. This function cannot guarantee full
 *                  compliance because I honestly can't read Haskell. However,
 *                  the documentation is quite extensive, so I orient myself at
 *                  those.
 *
 * END HEADER
 */

/**
 * The locatorLabels have been sourced from the Citr library. Basically it's just
 * a map with valid CSL locator labels and an array of possible natural labels
 * which a user might want to write (instead of the standardized labels).
 *
 * @var {{ [key: string]: string[] }}}
 */
const locatorLabels: Record<string, string[]> = {
  book: [ 'Buch', 'Bcher', 'B.', 'book', 'books', 'bk.', 'bks.', 'livre', 'livres', 'liv.' ],
  chapter: [ 'Kapitel', 'Kap.', 'chapter', 'chapters', 'chap.', 'chaps', 'chapitre', 'chapitres' ],
  column: [ 'Spalte', 'Spalten', 'Sp.', 'column', 'columns', 'col.', 'cols', 'colonne', 'colonnes' ],
  figure: [ 'Abbildung', 'Abbildungen', 'Abb.', 'figure', 'figures', 'fig.', 'figs' ],
  folio: [ 'Blatt', 'Bltter', 'Fol.', 'folio', 'folios', 'fol.', 'fols', 'f', 'f' ],
  issue: [ 'Nummer', 'Nummern', 'Nr.', 'number', 'numbers', 'no.', 'nos.', 'numro', 'numros', 'n', 'n' ],
  line: [ 'Zeile', 'Zeilen', 'Z', 'line', 'lines', 'l.', 'll.', 'ligne', 'lignes' ],
  note: [ 'Note', 'Noten', 'N.', 'note', 'notes', 'n.', 'nn.' ],
  opus: [ 'Opus', 'Opera', 'op.', 'opus', 'opera', 'opp.' ],
  page: [ 'Seite', 'Seiten', 'S.', 'page', 'pages', 'p.', 'pp.' ],
  paragraph: [ 'Absatz', 'Abstze', 'Abs.', '', '', 'paragraph', 'paragraphs', 'para.', 'paras', 'paragraphe', 'paragraphes', 'paragr.' ],
  part: [ 'Teil', 'Teile', 'part', 'parts', 'pt.', 'pts', 'partie', 'parties', 'part.' ],
  section: [ 'Abschnitt', 'Abschnitte', 'Abschn.', '', '', 'section', 'sections', 'sec.', 'secs', 'sect.' ],
  'sub verbo': [ 'sub verbo', 'sub verbis', 's.&#160;v.', 's.&#160;vv.', 's.v.', 's.vv.' ],
  verse: [ 'Vers', 'Verse', 'V.', 'verse', 'verses', 'v.', 'vv.', 'verset', 'versets' ],
  volume: [ 'Band', 'Bnde', 'Bd.', 'Bde.', 'volume', 'volumes', 'vol.', 'vols.' ]
}

/**
 * Citation detection: The first alternative matches "full" citations surrounded
 * by square brackets, whereas the second one matches in-text citations,
 * optionally with suffixes.
 *
 * * Group 1 matches regular "full" citations
 * * Group 2 matches in-text citations (not surrounded by brackets)
 * * Group 3 matches optional square-brackets suffixes to group 2 matches
 *
 * For more information, see https://pandoc.org/MANUAL.html#extension-citations
 *
 * @var {RegExp}
 */
const citationRE = /(?:\[([^[\]]*@[^[\]]+)\])|(?<=\s|^|(-))(?:@([\p{L}\d_][^\s]*[\p{L}\d_]|\{.+\})(?: +\[(.*?)\])?)/gum

/**
 * I hate everything at this. This can match every single possible variation on
 * whatever the f*** you can possibly do within square brackets according to the
 * documentation. I opted for named groups for these because otherwise I have no
 * idea what I have been doing here.
 *
 * * Group prefix: Contains the prefix, ends with a dash if we should suppress the author
 * * Group citekey: Contains the actual citekey, can be surrounded in curly brackets
 * * Group explicitLocator: Contains an explicit locator statement. If given, we MUST ignore any form of locator in the suffix
 * * Group explicitLocatorInSuffix: Same as above, but not concatenated to the citekey
 * * Group suffix: Contains the suffix, but may start with a locator (if explicitLocator and explicitLocatorInSuffix are not given)
 *
 * @var {RegExp}
 */
const fullCitationRE = /(?<prefix>.+)?(?:@(?<citekey>[\p{L}\d_][^\s{]*[\p{L}\d_]|\{.+\}))(?:\{(?<explicitLocator>.*)\})?(?:,\s+(?:\{(?<explicitLocatorInSuffix>.*)\})?(?<suffix>.*))?/u

/**
 * This regular expression matches locator ranges, like the following:
 *
 * * 23-45, and further (here it matches up to, not including the comma)
 * * 45
 * * 15423
 * * 14235-12532
 * * 12-34, 23, 56
 * * 12, 23-14, 23
 * * 12, 54, 12-23
 * * 1, 1-4
 * * 3
 * * NEW NEW NEW: Now also matches Roman numerals as sometimes used in forewords!
 *
 * @var {RegExp}
 */
const locatorRE = /^(?:[\d, -]*\d|[ivxlcdm, -]*[ivxlcdm])/i

/**
 * This is the return interface from this module: It declares a from-to position
 * indicating where in the original Markdown string the citation has been found
 * and an array citations which contains the parsed CSL Items that can be passed
 * to citeproc-js.
 */
export interface CitePosition {
  // The start position of this citation
  from: number
  // The end position of this citation
  to: number
  // The full source of this citation
  source: string
  // True if the citation should be composite
  composite: boolean
  // The list of cite items in CSL JSON style
  citations: CiteItem[]
}

/**
 * This interface describes the potential return of the parseSuffix function. It
 * can return a locator, a label, and a suffix. More specifically, it will return
 * a label in any case, defaulting to "page", just like citeproc.
 */
interface CiteItemSuffix {
  locator: string|undefined
  label: string
  suffix: string|undefined
}

// This is the same interface from citeproc.d.ts, but since the unit tests do not
// load the ambient declaration files, we have to declare it here as well. Doesn't
// hurt the linter.
interface CiteItem {
  id: string
  locator?: string
  label?: string
  'suppress-author'?: boolean
  'author-only'?: boolean
  prefix?: string
  suffix?: string
}

/**
 * This takes a suffix and extracts optional label and locator from this. Pass
 * true for the containsLocator property to indicate to this function that what
 * it got was not a regular suffix with an optional locator, but an explicit
 * locator so it knows it just needs to look for an optional label.
 *
 * @param   {string}          suffix           The suffix to parse
 * @param   {boolean}         containsLocator  If true, forces parseSuffix to return a locator
 *
 * @return  {CiteItemSuffix}                   An object containing three optional properties locator, label, or suffix.
 */
function parseSuffix (suffix: string|undefined, containsLocator: boolean): CiteItemSuffix {
  const retValue: CiteItemSuffix = {
    locator: undefined,
    label: 'page',
    suffix: undefined
  }

  if (suffix === undefined) {
    return retValue
  }

  // Make sure the suffix does not start or end with spaces
  suffix = suffix.trim()

  // If there is a label, the suffix must start with it
  for (const label in locatorLabels) {
    for (const natural of locatorLabels[label]) {
      if (suffix.toLowerCase().startsWith(natural.toLowerCase())) {
        retValue.label = label
        if (containsLocator) {
          // The suffix actually is the full locator, we just had to extract
          // the label from it. There is no remaining suffix.
          retValue.locator = suffix.substr(natural.length).trim()
        } else {
          // The caller indicated that this is a regular suffix, so we must also
          // extract the locator from what is left after label extraction.
          retValue.suffix = suffix.substr(natural.length).trim()
          const match = locatorRE.exec(retValue.suffix)
          if (match !== null) {
            retValue.locator = match[0] // Extract the full match
            retValue.suffix = retValue.suffix.substr(match[0].length).trim()
          }
        }

        return retValue // Early exit
      }
    }
  }

  // If we're here, there was no explicit label given, but the caller has indicated
  // that this suffix MUST contain a locator. This means that the whole suffix is
  // the locator.
  if (containsLocator) {
    retValue.locator = suffix
  } else {
    // The caller has not indicated that the whole suffix is the locator, so it
    // can be at the beginning. We only accept simple page/number ranges here.
    // For everything, the user should please be more specific.
    const match = locatorRE.exec(suffix)
    if (match !== null) {
      retValue.locator = match[0] // Full match is the locator
      retValue.suffix = suffix.substr(match[0].length).trim() // The rest is the suffix.
    }
  }

  return retValue
}

/**
 * Extracts citations from a string of Markdown, similar to how Citeproc does it.
 *
 * @param   {string}          markdown  The Markdown to parse
 *
 * @return  {CitePosition[]}            An array containing every found citation
 */
export default function extractCitations (markdown: string): CitePosition[] {
  const retValue = []

  for (const match of markdown.matchAll(citationRE)) {
    let from = match.index! // Here we know index will be set
    let to = from + match[0].length
    const citations = []
    let composite = false // Is set to true for in-text citations

    const fullCitation: string|undefined = match[1]
    const inTextSuppressAuthor: string|undefined = match[2]
    const inTextCitation: string|undefined = match[3]
    const optionalSuffix: string|undefined = match[4]

    // If we have an in-text citation and we should suppress the author, the
    // match.index does NOT include the positive lookbehind, so we have to manually
    // shift "from" to one before.
    if (inTextSuppressAuthor !== undefined) {
      from--
    }

    if (fullCitation !== undefined) {
      // We have a lame, full citation.
      for (const citationPart of fullCitation.split(';')) {
        const match = fullCitationRE.exec(citationPart.trim())
        if (match === null) {
          continue // Faulty citation
        }

        // We explicitly cast groups since we have groups in our RegExp and as
        // such the groups object will be set.
        const thisCitation: CiteItem = {
          id: (match.groups as any).citekey.replace(/{(.+)}/, '$1'),
          prefix: undefined,
          locator: undefined,
          label: 'page',
          'suppress-author': false,
          suffix: undefined
        }

        // First, deal with the prefix. The speciality here is that it can
        // indicate if we should suppress the author.
        const rawPrefix: string|undefined = (match.groups as any).prefix
        if (rawPrefix !== undefined) {
          thisCitation['suppress-author'] = rawPrefix.trim().endsWith('-')
          if (thisCitation['suppress-author']) {
            thisCitation.prefix = rawPrefix.substring(0, rawPrefix.trim().length - 1).trim()
          } else {
            thisCitation.prefix = rawPrefix.trim()
          }
        }

        // Second, deal with the suffix. This one can be much more tricky than
        // the prefix. We have three alternatives where the locator may be
        // present: If we have an explicitLocator or an explicitLocatorInSuffix,
        // we should extract the locator from there and leave the actual suffix
        // untouched. Only if those two alternatives are not present, then we
        // have a look at the rawSuffix and extract a (potential) locator.
        const explicitLocator: string|undefined = (match.groups as any).explicitLocator
        const explicitLocatorInSuffix: string|undefined = (match.groups as any).explicitLocatorInSuffix
        const rawSuffix: string|undefined = (match.groups as any).suffix

        let suffixToParse
        let containsLocator = true
        if (explicitLocator === undefined && explicitLocatorInSuffix === undefined) {
          // Potential locator in rawSuffix. Only in this case should we overwrite
          // the suffix (hence the same if-condition below)
          suffixToParse = rawSuffix
          containsLocator = false
        } else if (explicitLocatorInSuffix !== undefined || explicitLocator !== undefined) {
          suffixToParse = explicitLocator ?? explicitLocatorInSuffix
          thisCitation.suffix = rawSuffix?.trim()
        }

        const { label, locator, suffix } = parseSuffix(suffixToParse, containsLocator)
        thisCitation.locator = locator

        if (label !== undefined) {
          thisCitation.label = label
        }

        if (explicitLocator === undefined && explicitLocatorInSuffix === undefined) {
          thisCitation.suffix = suffix
        } else if (suffix !== undefined && thisCitation.locator !== undefined) {
          // If we're here, we should not change the suffix, but parseSuffix may
          // have put something into the suffix return. If we're here, that will
          // definitely be a part of the locator.
          thisCitation.locator += suffix
        }

        citations.push(thisCitation)
      }
    } else {
      // We have an in-text citation, so we can take a shortcut
      composite = true
      citations.push({
        prefix: undefined,
        id: inTextCitation.replace(/{(.+)}/, '$1'),
        'suppress-author': inTextSuppressAuthor !== undefined,
        ...parseSuffix(optionalSuffix, false) // Populate more depending on the suffix
      })
    }

    // After all of our yadda yadda, push the citation
    retValue.push({ from, to, citations, composite, source: match[0] })
  }

  return retValue
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        Footnote Parser
 * CVM-Role:        InlineParser
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     This inline parser adds footnote elements to the Lezer tree.
 *
 * END HEADER
 */

import { type InlineParser, type BlockParser } from '@lezer/markdown'
import { partialParse } from './partial-parse'

// TODO: Docs for this: https://github.com/lezer-parser/markdown#user-content-blockparser
export const footnoteParser: InlineParser = {
  // This parser should only match inline footnotes
  name: 'footnotes',
  before: 'Link', // [^1] will otherwise be detected as a link
  parse (ctx, next, pos) {
    if (next !== 91 && next !== 94) { // [, ^
      return -1
    }

    const relativePosition = pos - ctx.offset
    // Matches [^identifier] (alternative 1) and ^[inline] (alternative 2)
    const match = /\[\^[^\s]+?\]|\^\[.+?\]/.exec(ctx.text.slice(relativePosition))

    if (match === null || match.index > 0) {
      return -1
    }

    // At this point we have a footnote and it's at the current pos
    return ctx.addElement(ctx.elt('Footnote', pos, pos + match[0].length))
  }
}

export const footnoteRefParser: BlockParser = {
  name: 'footnote-refs',
  parse (ctx, line) {
    const match = /^\[\^[^\s]+\]:\s/.exec(line.text)
    if (match === null) {
      return false
    }

    const refFrom = ctx.lineStart

    const label = ctx.elt('FootnoteRefLabel', refFrom, ctx.lineStart + match[0].length - 1)

    const from = ctx.lineStart + match[0].length
    let to = ctx.lineStart + line.text.length // One newline less here

    const footnoteBody: string[] = [line.text.slice(match[0].length)]

    // Everything at least indented by 4 spaces OR empty lines belong to this paragraph
    while (ctx.nextLine() && /^\s{4,}|^\s*$/.test(line.text)) {
      footnoteBody.push(line.text)
      to += line.text.length + 1
    }

    // Remove trailing empty lines from the body itself
    let bodyTo = to
    while (footnoteBody.length > 0 && footnoteBody[footnoteBody.length - 1].trim() === '') {
      const lastline = footnoteBody.pop() as string
      bodyTo = bodyTo - lastline.length - 1
    }

    // Since footnotes can be empty, the above while loop will substract one too
    // much from empty footnotes (so that bodyTo = from - 1). Here we correct
    // for that.
    if (bodyTo < from) {
      bodyTo = from
    }

    const treeElem = partialParse(ctx, ctx.parser, footnoteBody.join('\n'), from)
    const body = ctx.elt('FootnoteRefBody', from, bodyTo, [treeElem])

    const wrapper = ctx.elt('FootnoteRef', refFrom, to, [ label, body ])
    ctx.addElement(wrapper)

    return true
  }
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        Frontmatter Parser
 * CVM-Role:        InlineParser
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     This block parser adds YAML frontmatter syntax tags.
 *
 * END HEADER
 */

import { type ParseWrapper, parseMixed } from '@lezer/common'
import { type BlockParser } from '@lezer/markdown'
import { yaml } from '@codemirror/lang-yaml'

// Adapted from: https://github.com/lezer-parser/markdown/blob/main/src/nest.ts
// This function looks out for parsed frontmatter nodes and starts an inner
// parse to add syntax highlighting to these.
export function yamlCodeParse (): ParseWrapper {
  const parser = yaml().language.parser
  return parseMixed((node, input) => {
    if (node.type.name === 'YAMLFrontmatter') {
      return { parser, overlay: node => node.type.name === 'CodeText' }
    } else {
      return null
    }
  })
}

/**
 * A BlockParser that looks out for frontmatter blocks and wraps them in a
 * YAMLFrontmatter and CodeText node. The parsing of the actual contents is done
 * with a separate parse wrapper (see above)
 */
export const frontmatterParser: BlockParser = {
  // We need to give the parser a name. Since it should only parse YAML frontmatters,
  // here we go.
  name: 'frontmatter',
  // It must run before the HorizontalRule block parser since the delimiters
  // can also be interpreted as horizontal rules.
  before: 'HorizontalRule',
  parse: (ctx, line) => {
    // This parser is inspired by the BlockParsers defined in
    // @lezer/markdown/src/markdown.ts
    if (line.text !== '---' || ctx.lineStart !== 0) {
      return false
    }

    // We have a possible YAML frontmatter. Now we need to look for the end of
    // the frontmatter (i.e. look for a line that is either --- or ...).
    // Meanwhile, we'll be collecting all lines encountered so that we can parse
    // them into a YAML AST.
    const yamlLines: string[] = []
    while (ctx.nextLine() && !/^(?:-{3}|\.{3})$/.test(line.text)) {
      yamlLines.push(line.text)
    }

    if (!/^(?:-{3}|\.{3})$/.test(line.text)) {
      // The parser has collected the full rest of the document. This means
      // the frontmatter never stopped. In order to maintain readability, we
      // simply abort parsing.
      return false
    }

    // A final check: A frontmatter is allowed to be empty, but it is NOT a
    // valid document if there is whitespace at the top (i.e. no blank lines
    // between the delimiters and the frontmatter content). Note that whitespace
    // *after* the frontmatter content is allowed.
    if (yamlLines.length > 0 && yamlLines[0].trim() === '') {
      return false
    }

    // At this point we have a correct and full YAML frontmatter, we know where
    // it starts and we know where it ends. In order to simplify creating the
    // required AST, we defer to letting the YAML parser parse this thing into
    // a tree that we can then simply convert into the format consumed by
    // Codemirror.

    const wrapperNode = ctx.elt('YAMLFrontmatter', 0, ctx.lineStart + 3, [
      ctx.elt('YAMLFrontmatterStart', 0, 3),
      ctx.elt('CodeText', 4, ctx.lineStart - 1),
      ctx.elt('YAMLFrontmatterEnd', ctx.lineStart, ctx.lineStart + 3)
    ])

    // Now that we are certain that we have a frontmatter, we must "consume" the
    // final line of the frontmatter so that the HorizontalRule parser cannot
    // detect this as a HorizontalRule (if the frontmatter ends with ---)
    ctx.nextLine()

    ctx.addElement(wrapperNode)
    return true // Signal that we've parsed this block
  }
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        Math Parser
 * CVM-Role:        InlineParser, BlockParser
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     This module provides an inline and a block parser for both
 *                  inline and block math.
 *
 * END HEADER
 */

import { type InlineParser, type BlockParser } from '@lezer/markdown'
import { StreamLanguage } from '@codemirror/language'
import { Tree } from '@lezer/common'
import { stexMath } from '@codemirror/legacy-modes/mode/stex'
import { partialParse } from './partial-parse'

const stexLang = StreamLanguage.define(stexMath)

export const inlineMathParser: InlineParser = {
  // This parser should only match inline-math (we have to divide that here)
  name: 'inlineMath',
  parse: (ctx, next, pos) => {
    if (next !== 36) { // $
      return -1
    }

    const inlineMathRE = /(?<![\\$])(?<dollar>\${1,2})(?![\s$])(?<eq>.+?)(?<![\s\\])\k<dollar>(?!\d)/g
    // Set the lastIndex to the relative position where we're currently parsing ...
    const relativePosition = pos - ctx.offset
    inlineMathRE.lastIndex = relativePosition

    // .. attempt a match ...
    const match = inlineMathRE.exec(ctx.text)
    // ... check that we had one, and that it is at the very start of the string ...
    if (match === null || match.index > relativePosition) {
      return -1 // There was either no match, or we're not yet there
    }

    // ... and work through the match.
    const { eq, dollar } = match.groups as Record<string, string>

    let curPos = pos
    const childNodes = [ctx.elt('CodeMark', curPos, curPos + dollar.length)]

    curPos += dollar.length
    const treeElem = partialParse(ctx, stexLang.parser, eq, curPos)
    childNodes.push(treeElem)

    curPos += eq.length
    childNodes.push(ctx.elt('CodeMark', curPos, curPos + dollar.length))
    const wrapperElem = ctx.elt('InlineCode', pos, curPos + dollar.length, childNodes)
    return ctx.addElement(wrapperElem)
  }
}

export const blockMathParser: BlockParser = {
  // We need to give the parser a name. Since it should only parse YAML frontmatters,
  // here we go.
  name: 'blockMath',
  parse: (ctx, line) => {
    const blockMathRE = /^(\s*\$\$)\s*$/
    // This parser is inspired by the BlockParsers defined in
    // @lezer/markdown/src/markdown.ts
    if (!blockMathRE.test(line.text)) {
      return false
    }

    // We have a possible math block
    const equationLines: string[] = []
    // We also need the position at which the block ends
    const startFrom = ctx.lineStart
    const from = ctx.lineStart + line.text.length + 1
    while (ctx.nextLine() && !blockMathRE.test(line.text)) {
      equationLines.push(line.text)
    }

    if (!blockMathRE.test(line.text)) {
      // The parser has collected the full rest of the document. This means
      // the math block never stopped. In order to maintain readability, we
      // simply abort parsing.
      return false
    }

    // Let the stex mode parse the block into a tree and create a new element
    // from that tree.
    const equation = equationLines.join('\n')
    const innerTree = stexLang.parser.parse(equation)

    // Here we detach the syntax tree from the containing `Document` node
    const firstChild = innerTree.children[0]
    let treeElem = ctx.elt(innerTree, from)
    if (firstChild instanceof Tree) {
      treeElem = ctx.elt(firstChild, from)
    }

    // Now create the wrapper
    const wrapperNode = ctx.elt('FencedCode', startFrom, ctx.lineStart + line.text.length, [
      ctx.elt('CodeMark', startFrom, from - 1), // Ignore the newline char (to ensure the math renderer can differentiate math blocks from code blocks)
      ctx.elt('CodeText', from, from + equation.length, [treeElem]),
      ctx.elt('CodeMark', Math.max(from + equation.length, ctx.lineStart), ctx.lineStart + line.text.length)
    ])

    ctx.addElement(wrapperNode)

    // Ensure the closing code mark is also contained within this block. NOTE:
    // Needs to be done AFTER we have used the line info to create the widget!
    ctx.nextLine()
    return true // Signal that we've parsed this block
  }
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        Image and Link parser
 * CVM-Role:        View
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     A small parser that can parse images and links that,
 *                  contrary to the built-in parser, allows spaces and non-
 *                  encoded characters in URLs. While this is the correct
 *                  behavior, Markdown has to deal with many local images and
 *                  links, and in there it's quite annoying either to have to
 *                  replace all spaces with %20, or have the images not work.
 *                  Here we basically implement a more "sloppy" parser that
 *                  gives our users the ability to paste readable, non-URL-
 *                  encoded file paths.
 *
 * END HEADER
 */

import { type InlineParser, type Element } from '@lezer/markdown'
import { markdown, markdownLanguage } from '@codemirror/lang-markdown'

const mdParser = markdown({ base: markdownLanguage }).language.parser

export const sloppyParser: InlineParser = {
  name: 'sloppy-parser', // Could be a fancy restaurant name or a bad one for a photographer
  before: 'Link',
  parse: (ctx, next, pos) => {
    if (next !== 33 && next !== 91) { // 33 == !, 91 == [
      return -1
    }

    const imgOrLinkRE = /^!?\[[^\]]+\]\(.+\)/i
    const relativeOffset = pos - ctx.offset
    const relativeSlice = ctx.text.slice(relativeOffset)
    const match = imgOrLinkRE.exec(relativeSlice)
    const isLink = next === 91

    if (match === null) {
      return -1
    }

    // NOTE: Since Markdown links can contain brackets, we cannot do this with
    // a RegExp-only solution (we could, but that might render us vulnerable to
    // infinite loops in the RegExp engine if done improperly). Henceforth, we
    // use RegExp only to detect whether there's an actually valid link at the
    // current context position. Next, we literally go char by char to find all
    // the elements we need

    const children: Element[] = []
    // Code Marks: ![
    let from = pos
    let to = from + 1
    children.push(ctx.elt('LinkMark', from, to))
    if (!isLink) {
      children.push(ctx.elt('LinkMark', ++from, ++to))
    }

    // Alt-text
    from = to
    to = from + ctx.text.slice(from - ctx.offset).indexOf(']')

    // The ALT-text can contain inline content (cf.
    // https://spec.commonmark.org/0.30/#example-515) so we have to run an
    // additional inline parse on it.
    const linkLabel = ctx.text.slice(from - ctx.offset, to - ctx.offset)
    const tree = mdParser.parse(linkLabel)
    const cursor = tree.cursor()
    const labelChildren: Element[] = []

    do {
      // ALT text can only contain inline nodes, but the parser will wrap
      // everything in both a Document and a Paragraph b/c it sees its as a
      // standalone document. Since the ALT text can only contain inline
      // elements, we skip the "Document" wrapper and only extract the children
      // of the next "Paragraph" element.
      if (cursor.name === 'Paragraph') {
        let currentChild = cursor.node.firstChild
        while (currentChild !== null) {
          // ctx.elt luckily also takes in an entire Tree, saving us a recursion
          labelChildren.push(
            ctx.elt(currentChild.toTree(), from + currentChild.from)
          )
          currentChild = currentChild.nextSibling
        }
        break
      }
    } while (cursor.next())

    children.push(ctx.elt('LinkLabel', from, to, labelChildren))

    // Code Marks: ](
    from = to
    to = from + 1
    children.push(ctx.elt('LinkMark', from, to))
    children.push(ctx.elt('LinkMark', ++from, ++to))

    // Perform our bracket matching magic 
    from = to
    let brackets = 1 // Count the opening bracket
    while (brackets > 0 && to < ctx.offset + ctx.text.length) {
      const c = ctx.text.charAt(to - ctx.offset)
      if (c === '(') {
        brackets++
      } else if (c === ')') {
        brackets--
      }
      to++
    }

    if (brackets > 0) {
      return -1 // The link didn't end until the end of the line
    }

    // Now, `to` points after the final bracket. The next check we have to do is
    // see if there's a title inside.
    let url = ctx.text.slice(from - ctx.offset, to - ctx.offset)

    if (/".+"\)$/.test(url)) {
      // We have a title
      to = from + url.indexOf('"') - 1
      children.push(ctx.elt('URL', from, to))

      from = to + 1
      to = from + 1
      children.push(ctx.elt('LinkMark', from, to))

      const titleLength = url.lastIndexOf('"') - url.indexOf('"') - 1
      from = to
      to = from + titleLength
      children.push(ctx.elt('LinkTitle', from, to))

      from = to
      to = from + 1
      children.push(ctx.elt('LinkMark', from, to))
      children.push(ctx.elt('LinkMark', ++from, ++to))
    } else {
      children.push(ctx.elt('URL', from, to - 1))
      children.push(ctx.elt('LinkMark', to - 1, to))
    }

    const wrapper = ctx.elt(isLink ? 'Link' : 'Image', pos, to, children)
    return ctx.addElement(wrapper)
  }
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        Pandoc table Parser
 * CVM-Role:        Block Parser
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     Since the default Markdown mode only offers limited support
 *                  for tables, this parser ensures we detect any kind of table
 *                  supported by Pandoc in a given document. The exception are
 *                  simple tables, both because they are hard to detect and as
 *                  other parsers will always take precedence. Tl;DR: Use pipe
 *                  or grid tables instead. That's what we have the TableEditor
 *                  for.
 *
 * END HEADER
 */

import {
  type BlockParser,
  type Element,
  type BlockContext,
  type LeafBlockParser
} from '@lezer/markdown'

// Group 1: ^| table contents |$; Group 2: some text | with pipes in between
const pipeRE = /^(\|.+?\|)$|(.+?\|.+?)/
const pipeHeaderRE = /^[|+:-]+$/
const gridLineRE = /^\+[-=+:]+\+$/
const gridContentRE = /^\|.+\|$/

/**
 * Parses a grid table and returns a subtree that can be used for syntax highlighting
 *
 * @param   {BlockContext}  ctx    The context
 * @param   {number}        pos    Where the table starts in the document
 * @param   {number}        end    Where the table ends in the document
 * @param   {string[]}      lines  The list of lines belonging to the table
 *
 * @return  {Element}              The subtree
 */
function parseGridTable (ctx: BlockContext, pos: number, end: number, lines: string[]): Element {
  const rows: Element[] = []
  // Grid Tables alternate between separator lines and content lines. Content
  // lines can span multiple rows
  let from = pos
  let to = pos + lines[0].length
  for (const line of lines) {
    to = from + line.length
    const isSeparator = gridLineRE.test(line)
    if (isSeparator) {
      const sep = ctx.elt('TableDelimiter', from, to)
      rows.push(ctx.elt('TableRow', from, to, [sep]))
    } else {
      // Content line -> move through the line and mark delimiters as we see them
      const children: Element[] = [ctx.elt('TableDelimiter', from, from + 1)]
      let cellFrom = from + 1
      let cellTo = cellFrom
      for (const ch of line.substring(1)) {
        if (ch === '|') {
          children.push(ctx.elt('TableCell', cellFrom, cellTo))
          children.push(ctx.elt('TableDelimiter', cellTo, cellTo + 1))
          cellFrom = cellTo
        }
        cellTo++
      }
      rows.push(ctx.elt('TableRow', from, to, children))
    }
    from = to + 1
  }
  return ctx.elt('Table', pos, end, rows)
}

/**
 * Parses a pipe table and returns a subtree that can be used for syntax highlighting
 *
 * @param   {BlockContext}  ctx    The context
 * @param   {number}        pos    Where the table starts in the document
 * @param   {number}        end    Where the table ends in the document
 * @param   {string[]}      lines  The list of lines belonging to the table
 *
 * @return  {Element}              The subtree
 */
function parsePipeTable (ctx: BlockContext, pos: number, end: number, lines: string[]): Element {
  const rows: Element[] = []
  // For pipe tables, the first row is always the header, the second always the
  // delimiter, afterwards only content cells.
  // const header = ctx.elt('TableHeader')
  let from = pos
  let to = pos + lines[0].length
  let isFirstLine = true
  let isHeaderLine = false
  for (const line of lines) {
    to = from + line.length + 1
    if (isHeaderLine) {
      const delim = ctx.elt('TableDelimiter', from, to)
      rows.push(ctx.elt('TableRow', from, to, [delim]))
      isHeaderLine = false
      from = to
      continue
    }

    const children: Element[] = []
    let cellFrom = from
    let cellTo = from
    for (const ch of line) {
      if (ch === '|' && cellTo > from) {
        const type = isFirstLine ? 'TableHeader' : 'TableCell'
        children.push(ctx.elt(type, cellFrom, cellTo))
        children.push(ctx.elt('TableDelimiter', cellTo, cellTo + 1))
        cellFrom = cellTo + 1
      } else if (ch === '|' && cellFrom === from) {
        children.push(ctx.elt('TableDelimiter', cellFrom, cellFrom + 1))
        cellFrom++
      }
      cellTo++
    }

    rows.push(ctx.elt('TableRow', from, to, children))
    from = to

    if (isFirstLine) {
      isHeaderLine = true
      isFirstLine = false
    }
  }
  return ctx.elt('Table', pos, end, rows)
}

export const gridTableParser: BlockParser = {
  name: 'grid-table',
  parse: (ctx, line) => {
    // Let's begin with the easiest thing to detect: grid tables
    if (!gridLineRE.test(line.text)) {
      return false
    }
    // We have a potential grid table. The end of the table is being marked by
    // the last line that matches a grid line.
    const lines: string[] = [line.text]
    const start = ctx.lineStart
    // We have alternating lines with +---+ and | cell |
    while (ctx.nextLine() && (gridLineRE.test(line.text) || gridContentRE.test(line.text))) {
      lines.push(line.text)
    }

    if (lines.length < 3) {
      return false // Grid tables must span at least three lines
    }

    const end = ctx.lineStart + line.text.length

    const elt = parseGridTable(ctx, start, end, lines)
    ctx.addElement(elt)
    return true
  }
}

const pipeLeafParser: LeafBlockParser = {
  nextLine (ctx, line, leaf) {
    // Pipe tables are only finished on empty lines, i.e. we don't have to do
    // any logic in here.
    return false
  },
  finish (ctx, leaf) {
    // Called when there is an empty line, or something similar. At this point
    // we need to check that whatever is in the leaf block is a valid pipe table
    const lines = leaf.content.split('\n')
    if (lines.length < 3) {
      return false // Pipe tables must have at least three lines
    }

    if (!pipeHeaderRE.test(lines[1])) {
      return false // Second line must be a pipe table header
    }

    // All other lines must conform to pipeRE
    for (let i = 0; i < lines.length; i++) {
      if (i === 1) {
        continue
      }

      if (!pipeRE.test(lines[i])) {
        return false
      }
    }

    // Construct the pipe table
    const elt = parsePipeTable(ctx, leaf.start, leaf.start + leaf.content.length, lines)
    ctx.addLeafElement(leaf, elt)
    return true
  }
}

export const pipeTableParser: BlockParser = {
  name: 'pipe-table',
  leaf (ctx, leaf) {
    if (pipeRE.test(leaf.content)) {
      // NOTE: This will not detect "full" or "regular" pipe tables, since these
      // will already be handled by the GFM table parser. This parser therefore
      // basically only takes care of the "ugly" pipe tables (where the outer
      // pipes are omitted). So don't wonder if out of your test pipe tables
      // only some are detected, that's the reason.
      return pipeLeafParser
    } else {
      return null
    }
  }
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        Zettelkasten Link Parser
 * CVM-Role:        InlineParser
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     A small parser that adds Zettelkasten link elements to the tree.
 *
 * END HEADER
 */

import { type InlineParser } from '@lezer/markdown'

export interface ZknLinkParserConfig {
  /**
   * Describes whether internal Zettelkasten/Wiki-Links should follow the format
   * `[[link|title]]`, or `[[title|link]]`. If not provided, the parser falls
   * back to the default `[[link|title]]`.
   *
   * * The `[[link|title]]` syntax is used by:
   *   * Obsidian
   *   * Wikipedia (MediaWiki)
   *   * VimWiki
   *   * Orgmode
   * * The `[[title|link]]` syntax is used by:
   *   * GitHub
   */
  format?: 'link|title'|'title|link'
}

// This parser adds Zettelkasten links to the syntax tree.
export const zknLinkParser = function (config?: ZknLinkParserConfig): InlineParser {
  return {
    // This parser should only match citations
    name: 'zkn-links',
    before: 'Link', // In case of default [[links]], the inner brackets would be detected as links
    parse: (ctx, next, pos) => {
      const currentOffset = pos - ctx.offset
      const restOfLine = ctx.text.slice(currentOffset)
      if (!restOfLine.startsWith('[[')) {
        return -1
      }

      if (restOfLine.indexOf(']]') < 2) {
        return -1
      }

      const from = pos
      const to = from + restOfLine.indexOf(']]') + 2

      // [[
      const startFrom = pos
      const startTo = startFrom + 2

      // ]]
      const endFrom = pos + restOfLine.indexOf(']]')
      const endTo = endFrom + 2

      // Populate the children array
      const children = [
        ctx.elt('CodeMark', startFrom, startTo),
        ctx.elt('CodeMark', endFrom, endTo)
      ]

      // Link contents
      const contentFrom = startTo
      const contentTo = endFrom
      const contents = ctx.text.slice(contentFrom - ctx.offset, contentTo - ctx.offset)

      const pipeIdx = contents.indexOf('|')
      // NOTE: In order to avoid either empty links or empty titles and having
      // to deal with these edge cases, we disallow putting pipes at either the
      // beginning or the end of a link.
      if (pipeIdx > 0 && !contents.endsWith('|')) {
        // The link contains both a link and a title.
        const titleFirst = config?.format === 'title|link'
        children.splice(1, 0,
          ctx.elt(titleFirst ? 'ZknLinkTitle' : 'ZknLinkContent', contentFrom, contentFrom + pipeIdx),
          ctx.elt('ZknLinkPipe', contentFrom + pipeIdx, contentFrom + pipeIdx + 1),
          ctx.elt(titleFirst ? 'ZknLinkContent' : 'ZknLinkTitle', contentFrom + pipeIdx + 1, contentTo)
        )
      } else {
        // The link equals the title, no pipe found
        children.splice(1, 0, ctx.elt('ZknLinkContent', contentFrom, contentTo))
      }

      const wrapper = ctx.elt('ZknLink', from, to, children)

      return ctx.addElement(wrapper)
    }
  }
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        Pandoc Attributes parser
 * CVM-Role:        View
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     Pandoc supports attributes for various elements in order to
 *                  add metadata to elements. For example, headers can receive a
 *                  custom identifier by specifying it in curly braces
 *                  afterwards, code-blocks can receive more metadata by
 *                  wrapping the info string in curly braces, and images can
 *                  receive specific width and height metrics. This inline
 *                  parser here parses these into tree elements that can then be
 *                  styled.
 *
 * END HEADER
 */

import { type InlineParser } from '@lezer/markdown'

/**
 * Parses Pandoc attribute strings (e.g. `{.unnumbered}`) in the code
 */
export const pandocAttributesParser: InlineParser = {
  name: 'pandoc-attributes',
  parse: (ctx, next, pos) => {
    if (next !== 123) { // 123 === {
      return -1
    }

    if (pos === ctx.offset) {
      return -1 // Curly brackets must only *follow* something.
    }

    // Ensure a matching closing bracket.
    const relativeOffset = pos - ctx.offset
    if (ctx.text.indexOf('}') <= relativeOffset) {
      return -1
    }

    const end = ctx.offset + ctx.text.indexOf('}') + 1

    // Final check: Pandoc attributes must be either the last thing on the line
    // (then they basically apply to the whole line, i.e. with code block meta),
    // or directly preceeded by a non-whitespace symbol.
    const isWhitespaceAfter = /^\s*$/.test(ctx.text.slice(ctx.text.indexOf('}') + 1))
    if (/\s/.test(ctx.text[relativeOffset - 1]) && !isWhitespaceAfter) {
      return -1
    }

    const inlineCode = ctx.elt('InlineCode', pos, end, [
      ctx.elt('CodeMark', pos, pos + 1),
      ctx.elt('CodeMark', end - 1, end)
    ])

    return ctx.addElement(ctx.elt('PandocAttribute', pos, end, [inlineCode]))
  }
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        Citation Parser
 * CVM-Role:        InlineParser
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     This inline parser adds highlight elements to the tree
 *
 * END HEADER
 */

import { type InlineParser, type DelimiterType } from '@lezer/markdown'

const MarkDelimiter: DelimiterType = {
  resolve: 'HighlightContent',
  mark: 'HighlightMark' // No specific syntax node (for now due to backwards compatibility reasons)
}

/**
 * Determines if the provided char is allowed to surround highlighting marks
 *
 * @param   {number}   c  The char code
 *
 * @return  {boolean}     Whether it's allowed before/after a highlight mark
 */
function allowedSurroundingChar (c: number): boolean {
  const char = String.fromCharCode(c)
  // Regex matches whitespace (Z), non-word-characters (math, currentcy; S), and
  // general punctuation characters. NOTE: Partially "stolen" and adapted from
  // the Lezer Markdown parser.
  return /\p{Z}|\p{S}|\p{P}/u.test(char)
}

export const highlightParser: InlineParser = {
  name: 'highlights',
  parse: (ctx, next, pos) => {
    // The next char must be either a colon or an equal sign
    if (next !== 58 /* : */ && next !== 61 /* = */) {
      return -1
    }

    // The one following `next` must be the same character
    if (pos === ctx.end || next !== ctx.char(pos + 1)) {
      return -1
    }

    // A highlight marker is considered opening if it is at the beginning of the
    // line (bol) or is preceded by whitespace or any punctuation or generally
    // non-word characters. Furthermore, it must not be followed by whitespace
    // (or punctuation/certain chars) and not be at the end of the line (eol).
    // For a highlight marker to be considered closing, it needs the opposite
    // requirements. This is why we need both checks (because otherwise closing
    // tags would allow preceding whitespace which would prompt Pandoc not to
    // render them).
    const bol = pos === ctx.offset
    const eol = pos + 2 === ctx.end
    const validBefore = bol || (pos > ctx.offset && allowedSurroundingChar(ctx.char(pos - 1)))
    const validAfter = eol || (pos + 1 < ctx.end && allowedSurroundingChar(ctx.char(pos + 2)))

    const isOpening = validBefore && !validAfter
    const isClosing = validAfter

    return ctx.addDelimiter(MarkDelimiter, pos, pos + 2, isOpening, isClosing)
  }
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        Zettelkasten Tag Parser
 * CVM-Role:        InlineParser
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     A small parser that adds Zettelkasten tags to the tree.
 *
 * END HEADER
 */

import { type InlineParser } from '@lezer/markdown'

// Any character allowed before a tag (the first are space and nbsp)
const allowedCharsBefore = ' \t\n({['.split('')
const tagRE = /^##?[^\s,.:;!?"'`@$%&*#^+~\\/|<=>[\](){}]+#?/u

export const zknTagParser: InlineParser = {
  name: 'zkn-tags',
  parse: (ctx, next, pos) => {
    if (next !== 35) { // #
      return -1
    }

    // Check the character before
    if (pos > ctx.offset && !allowedCharsBefore.includes(ctx.slice(pos - 1, pos))) {
      return -1
    }

    const currentOffset = pos - ctx.offset
    const restOfLine = ctx.text.slice(currentOffset)

    const match = tagRE.exec(restOfLine)
    if (match === null) {
      return -1
    }

    const end = pos + match[0].length
    // NOTE: Apparently syntax themes only style leaf nodes, not containers, so
    // we'll wrap the whole tag into a single ZknTagContent.
    // const markElem = ctx.elt('CodeMark', pos, pos + 1)
    const content = ctx.elt('ZknTagContent', pos, end)
    const tag = ctx.elt('ZknTag', pos, end, [content])
    return ctx.addElement(tag)
  }
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        parseNode
 * CVM-Role:        Utility Function
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     This file contains a converter that can take in a Markdown
 *                  source string and convert it to an Abstract Syntax Tree.
 *                  This file follows the advice of Marijn Haverbeke, who
 *                  mentioned that Lezer trees are not necessarily "abstract"
 *                  and hence need to be converted prior to utilizing the tree:
 *
 *                  "These trees, represented by data structures from the
 *                  @lezer/common package, are more limited than the abstract
 *                  syntax trees you might have seen in other contexts. They are
 *                  not very abstract."
 *                  (from: https://lezer.codemirror.net/docs/guide/)
 *
 *                  The utility function runs the Markdown parser as defined for
 *                  the main editor to ensure that every element that the user
 *                  can see in the editor will also end up represented here. The
 *                  syntax tree is primarily used in two different instances
 *                  across the app:
 *
