import extractCitations, { type CitePosition } from '@common/util/extract-citations'
import { type SyntaxNode } from '@lezer/common'
import { parseTableNode } from './parse-table-node'
import { getWhitespaceBeforeNode } from './get-whitespace-before-node'
import { genericTextNode } from './generic-text-node'
import { parseChildren } from './parse-children'

/**
 * Basic info every ASTNode needs to provide
 */
export interface MDNode {
  /**
   * The node.name property (may differ from the type; significant mainly for
   * generics)
   */
  name: string
  /**
   * The start offset of this node in the original source
   */
  from: number
  /**
   * The end offset of this node in the original source
   */
  to: number
  /**
   * This property contains the whitespace before this node; required to
   * determine appropriate significant whitespace portions for some elements
   * upon converting to HTML.
   */
  whitespaceBefore: string
  /**
   * Can be used to store arbitrary attributes (e.g. Pandoc-style attributes
   * such as {.className})
   */
  attributes?: Record<string, string>
}

/**
 * Represents a footnote (the indicator within the text itself, not the
 * reference).
 */
export interface Footnote extends MDNode {
  type: 'Footnote'
  /**
   * If this is true, this means that the label is actually the footnote's
   * context, whereas label will be the footnote ref number if its false.
   */
  inline: boolean
  /**
   * The label of the footnote (sans the formatting, i.e. [^1] -> 1)
   */
  label: string
}

/**
 * A footnote reference, complete with label and footnote body.
 */
export interface FootnoteRef extends MDNode {
  type: 'FootnoteRef'
  /**
   * The label of the footnote (sans the formatting, i.e. [^1]: -> 1)
   */
  label: string
  /**
   * A list of children representing the footnote's body
   */
  children: ASTNode[]
}

/**
 * Either a link or an image, since the difference between these two nodes
 * consists of a single character.
 */
export interface LinkOrImage extends MDNode {
  type: 'Link'|'Image'
  /**
   * The URL of the link or image
   */
  url: string
  /**
   * ALT text of the link or image (i.e. what's written in square brackets)
   */
  alt: TextNode
  /**
   * Optional title text (i.e. what can be added after the URL in quotes)
   */
  title?: TextNode
}

/**
 * Represents a Heading.
 */
export interface Heading extends MDNode {
  type: 'Heading'
  /**
   * The heading's content
   */
  value: TextNode
  /**
   * Level from 1-6
   */
  level: number
}

/**
 * A citation element
 */
export interface Citation extends MDNode {
  type: 'Citation'
  /**
   * The unparsed, raw citation code
   */
  value: string
  /**
   * The parsed citation code that can be used to render the citation
   */
  parsedCitation: CitePosition
}

/**
 * A highlight, e.g., encapsulated ==in equality signs==
 */
export interface Highlight extends MDNode {
  type: 'Highlight'
  /**
   * Since it's a regular inline element, it can have children
   */
  children: ASTNode[]
}

export interface Superscript extends MDNode {
  type: 'Superscript'
  children: ASTNode[]
}

export interface Subscript extends MDNode {
  type: 'Subscript'
  children: ASTNode[]
}

/**
 * A single list item.
 */
export interface ListItem extends MDNode {
  type: 'ListItem'
  /**
   * An optional property. If it exists, it is a task item, and the property
   * indicates whether it is checked or not.
   */
  checked?: boolean
  /**
   * An optional property. It is set on ordered list items and indicates the
   * number that was used for this item in the Markdown source. Should be
   * ignored by converters that transform the list into HTML.
   */
  number?: number
  /**
   * A property that includes information about the list item marker.
   */
  marker: {
    /**
     * The start of the list marker.
     */
    from: number
    /**
     * The end of the list marker.
     */
    to: number
  }
  /**
   * A list item can contain an arbitrary amount of child nodes. Adding "List"
   * as an explicit child to signify that nested lists are children of an item.
   */
  children: Array<OrderedList|BulletList|ASTNode>
}

/**
 * Represents an ordered list.
 */
export interface OrderedList extends MDNode {
  type: 'OrderedList'
  /**
   * At what number the list starts (default: 1)
   */
  startsAt: number
  /**
   * The delimiter used by this list, can be either ) or .
   */
  delimiter: ')'|'.'
  /**
   * Whether this list is loose (in that case, HTML output should wrap the list
   * item's contents in paragraphs)
   */
  loose: boolean
  /**
   * A set of list items
   */
  items: ListItem[]
}

export interface BulletList extends MDNode {
  type: 'BulletList'
  /**
   * The symbol this list uses
   */
  symbol: '*'|'-'|'+'
  /**
   * Whether this list is loose (in that case, HTML output should wrap the list
   * item's contents in paragraphs)
   */
  loose: boolean
  /**
   * A set of list items
   */
  items: ListItem[]
}

/**
 * Represents a fenced code. NOTE that CodeBlocks are also treated as FencedCode.
 */
export interface FencedCode extends MDNode {
  type: 'FencedCode'
  /**
   * The info string (can be an empty string, e.g., for indented code)
   */
  info: string
  /**
   * The verbatim source code. (Not represented as a TextNode since whitespace
   * is significant and it shouldn't count towards word counts, etc.)
   */
  source: string
}

/**
 * Represents inline code.
 */
export interface InlineCode extends MDNode {
  type: 'InlineCode'
  /**
   * The verbatim source code. (Not represented as a TextNode since whitespace
   * is significant and it shouldn't count towards word counts, etc.)
   */
  source: string
}

/**
 * An emphasis node (italic or bold).
 */
export interface Emphasis extends MDNode {
  type: 'Emphasis'
  /**
   * The type of emphasis -- italic or bold
   */
  which: 'italic'|'bold'
  /**
   * The children of this node
   */
  children: ASTNode[]
}

/**
 * This node represents a YAML frontmatter. It shares a lot with the FencedCode
 * type, i.e. the YAML code will not be parsed into an object.
 */
export interface YAMLFrontmatter extends MDNode {
  type: 'YAMLFrontmatter'
  /**
   * The verbatim YAML source.
   */
  source: string
}

/**
 * Represents a single table cell
 */
export interface TableCell extends MDNode {
  type: 'TableCell'
  /**
   * The text content of the cell TODO: Arbitrary children!
   */
  children: ASTNode[]
}

/**
 * Represents a table row.
 */
export interface TableRow extends MDNode {
  type: 'TableRow'
  /**
   * This is set to true if the row is a header.
   */
  isHeaderOrFooter: boolean
  /**
   * A list of cells within this row
   */
  cells: TableCell[]
}

/**
 * Represents a table element.
 */
export interface Table extends MDNode {
  type: 'Table'
  /**
   * A list of rows of this table
   */
  rows: TableRow[]
  /**
   * A list of column alignments in the table. May be undefined; the default is
   * for all columns to be left-aligned.
   */
  alignment?: Array<'left'|'center'|'right'>
  /**
   * This property can optionally contain the table type in the source.
   */
  tableType?: 'grid'|'pipe'
}

/**
 * Represents a ZettelkastenLink (`[[Some file.md]]`)
 */
export interface ZettelkastenLink extends MDNode {
  type: 'ZettelkastenLink'
  /**
   * Contains the raw contents of the link
   */
  value: string
  /**
   * The link title; may be the same as value
   */
  title: TextNode
}

/**
 * Represents a tag (`#some-tag`)
 */
export interface ZettelkastenTag extends MDNode {
  type: 'ZettelkastenTag'
  /**
   * Contains the raw contents of the tag
   */
  value: string
}

export interface Comment extends MDNode {
  type: 'Comment'
  /**
   * Contains the raw contents of the comment
   */
  value: string
}

/**
 * A generic text node that can represent a string of content. Most nodes
 * contain at least one TextNode as its content (e.g. emphasis).
 */
export interface TextNode extends MDNode {
  type: 'Text'
  /**
   * The string value of the text node.
   */
  value: string
}

/**
 * This generic node represents any Lezer node that has no specific role (or can
 * be handled without additional care). This ensures that new nodes will always
 * end up in the resulting AST, even if we forgot to add the node specifically.
 */
export interface GenericNode extends MDNode {
  type: 'Generic'
  /**
   * Each generic node may have children
   */
  children: ASTNode[]
}

/**
 * Any node that can be part of the AST is an ASTNode.
 */
export type ASTNode = Comment | Footnote | FootnoteRef | LinkOrImage | TextNode
| Heading | Citation | Highlight | Superscript | Subscript | OrderedList
| BulletList | ListItem | GenericNode | FencedCode | InlineCode | YAMLFrontmatter
| Emphasis | Table | TableCell | TableRow | ZettelkastenLink | ZettelkastenTag
/**
 * Extract the "type" properties from the ASTNodes that can differentiate these.
 */
export type ASTNodeType = ASTNode['type']

/**
 * Parses a single Lezer style SyntaxNode to an ASTNode.
 *
 * @param   {SyntaxNode}  node      The node to convert
 * @param   {string}      markdown  The Markdown source, required to extract the
 *                                  actual text content of the SyntaxNodes,
 *                                  which isn't stored in the nodes themselves.
 *
 * @return  {ASTNode}               The root node of a Markdown AST
 */
export function parseNode (node: SyntaxNode, markdown: string): ASTNode {
  switch (node.name) {
    // NOTE: Most nodes are treated as generics (see default case); here we only
    // define nodes which we can "compress" a little bit or make accessible
    case 'Image':
    case 'Link': {
      const alt = node.getChild('LinkLabel')
      const url = node.getChild('URL')
      if (url === null) {
        return {
          type: 'Generic',
          name: node.name,
          from: node.from,
          to: node.to,
          whitespaceBefore: getWhitespaceBeforeNode(node, markdown),
          children: [genericTextNode(node.from, node.to, markdown.substring(node.from, node.to))]
        }
      }

      const astNode: LinkOrImage = {
        type: node.name,
        name: node.name,
        from: node.from,
        to: node.to,
        whitespaceBefore: getWhitespaceBeforeNode(node, markdown),
        // title: genericTextNode(node.from, node.to, markdown.substring(node.from, node.to)), TODO
        url: markdown.substring(url.from, url.to),
        alt: alt !== null
          ? genericTextNode(alt.from, alt.to, markdown.substring(alt.from, alt.to))
          : genericTextNode(url.from, url.to, markdown.substring(url.from, url.to))
      }

      const marks = node.getChildren('LinkMark')

      if (alt === null && marks.length >= 2) {
        // The default Markdown parser doesn't apply "LinkLabel" unfortunately.
        // So instead we have to get whatever is in between the first and second
        // linkMark.
        astNode.alt = genericTextNode(marks[0].to, marks[1].from, markdown.substring(marks[0].to, marks[1].from))
      } // Else: Somewhat malformed link.

      return astNode
    }
    case 'URL': {
      const astNode: LinkOrImage = {
        type: 'Link',
        name: node.name,
        from: node.from,
        to: node.to,
        whitespaceBefore: getWhitespaceBeforeNode(node, markdown),
        // title: genericTextNode(node.from, node.to, markdown.substring(node.from, node.to)), TODO
        url: markdown.substring(node.from, node.to),
        alt: genericTextNode(node.from, node.to, markdown.substring(node.from, node.to))
      }
      return astNode
    }
    case 'ATXHeading1':
    case 'ATXHeading2':
    case 'ATXHeading3':
    case 'ATXHeading4':
    case 'ATXHeading5':
    case 'ATXHeading6': {
      const mark = node.getChild('HeaderMark')
      const level = mark !== null ? mark.to - mark.from : 0
      const astNode: Heading = {
        type: 'Heading',
        name: node.name,
        from: node.from,
        to: node.to,
        whitespaceBefore: getWhitespaceBeforeNode(node, markdown),
        value: genericTextNode(mark?.to ?? node.from, node.to, markdown.substring(mark?.to ?? node.from, node.to).trim()),
        level
      }
      return astNode
    }
    case 'SetextHeading1':
    case 'SetextHeading2': {
      const mark = node.getChild('HeaderMark')
      const level = mark !== null && markdown.substring(mark.from, mark.to).includes('-') ? 2 : 1
      const astNode: Heading = {
        type: 'Heading',
        name: node.name,
        from: node.from,
        to: node.to,
        whitespaceBefore: getWhitespaceBeforeNode(node, markdown),
        value: genericTextNode(mark?.to ?? node.from, node.to, markdown.substring(node.from, mark?.from ?? node.to).trim()),
        level
      }
      return astNode
    }
    case 'Citation': {
      const astNode: Citation = {
        name: 'Citation',
        type: 'Citation',
        value: markdown.substring(node.from, node.to),
        parsedCitation: extractCitations(markdown.substring(node.from, node.to))[0],
        from: node.from,
        to: node.to,
        whitespaceBefore: getWhitespaceBeforeNode(node, markdown)
      }
      return astNode
    }
    case 'Footnote': {
      const contents = markdown.substring(node.from + 2, node.to - 1) // [^1] --> 1
      const astNode: Footnote = {
        type: 'Footnote',
        name: 'Footnote',
        from: node.from,
        inline: contents.endsWith('^'),
        to: node.to,
        whitespaceBefore: getWhitespaceBeforeNode(node, markdown),
        label: contents.endsWith('^') ? contents.substring(0, contents.length - 1) : contents
      }
      return astNode
    }
    case 'FootnoteRef': {
      const label = node.getChild('FootnoteRefLabel')
      const body = node.getChild('FootnoteRefBody')
      const astNode: FootnoteRef = {
        type: 'FootnoteRef',
        name: 'FootnoteRef',
        from: node.from,
        to: node.to,
        whitespaceBefore: getWhitespaceBeforeNode(node, markdown),
        label: label !== null ? markdown.substring(label.from + 2, label.to - 2) : '',
        children: []
      }

      if (body !== null) {
        return parseChildren(astNode, body, markdown)
      } else {
        return astNode
      }
    }
    case 'HighlightContent': {
      const astNode: Highlight = {
        type: 'Highlight',
        name: 'Highlight',
        from: node.from,
        to: node.to,
        whitespaceBefore: getWhitespaceBeforeNode(node, markdown),
        children: []
      }
      return parseChildren(astNode, node, markdown)
    }
    case 'OrderedList': {
      const astNode: OrderedList = {
        type: 'OrderedList',
        startsAt: 0,
        delimiter: '.',
        loose: false, // TODO
        name: node.name,
        from: node.from,
        to: node.to,
        whitespaceBefore: getWhitespaceBeforeNode(node, markdown),
        items: []
      }

      for (const item of node.getChildren('ListItem')) {
        const listItem: ListItem = {
          type: 'ListItem',
          name: 'ListItem',
          from: item.from,
          to: item.to,
          whitespaceBefore: getWhitespaceBeforeNode(node, markdown),
          children: [],
          marker: { from: item.from, to: item.from }
        }

        const listMark = item.getChild('ListMark')
        if (listMark !== null) {
          listItem.marker.from = listMark.from
          listItem.marker.to = listMark.to

          const number = parseInt(markdown.substring(listMark.from, listMark.to - 1), 10)
          const delim = markdown.substring(listMark.to - 1, listMark.to)
          listItem.number = number
          if (astNode.startsAt < 1) {
            astNode.startsAt = number
            if (delim === ')' || delim === '.') {
              astNode.delimiter = delim
            }
          }
        }

        // Identify potential task item
        const task = item.getChild('Task')
        const taskMarker = task !== null ? task.getChild('TaskMarker') : null
        if (taskMarker !== null) {
          const text = markdown.substring(taskMarker.from, taskMarker.to)
          listItem.checked = text === '[x]'
        }

        astNode.items.push(parseChildren(listItem, item, markdown))
      }

      return astNode
    }
    case 'BulletList': {
      const astNode: BulletList = {
        type: 'BulletList',
        symbol: '-',
        loose: false, // TODO
        name: node.name,
        from: node.from,
        to: node.to,
        whitespaceBefore: getWhitespaceBeforeNode(node, markdown),
        items: []
      }

      for (const item of node.getChildren('ListItem')) {
        const listItem: ListItem = {
          type: 'ListItem',
          name: 'ListItem',
          from: item.from,
          to: item.to,
          whitespaceBefore: getWhitespaceBeforeNode(node, markdown),
          children: [],
          marker: { from: item.from, to: item.from }
        }

        const listMark = item.getChild('ListMark')
        if (listMark !== null) {
          listItem.marker.from = listMark.from
          listItem.marker.to = listMark.to

          const symbol = markdown.substring(listMark.from, listMark.to)
          if (symbol === '-' || symbol === '+' || symbol === '*') {
            astNode.symbol = symbol
          }
        }

        // Identify potential task item
        const task = item.getChild('Task')
        const taskMarker = task !== null ? task.getChild('TaskMarker') : null
        if (taskMarker !== null) {
          const text = markdown.substring(taskMarker.from, taskMarker.to)
          listItem.checked = text === '[x]'
        }

        astNode.items.push(parseChildren(listItem, item, markdown))
      }

      return astNode
    }
    case 'FencedCode':
    case 'CodeBlock': {
      let info = node.getChild('CodeInfo')
      const mark = node.getChild('CodeMark')
      if (mark !== null) {
        const codeMark = markdown.substring(mark.from, mark.to)
        if (codeMark === '$$') {
          // Exchange the (nonexistent) infostring with the double-dollars so
          // that consumers can detect that this is MathTex
          info = mark
        }
      }
      const source = node.getChild('CodeText')
      const astNode: FencedCode = {
        type: 'FencedCode',
        name: 'FencedCode',
        from: node.from,
        to: node.to,
        whitespaceBefore: getWhitespaceBeforeNode(node, markdown),
        info: info !== null ? markdown.substring(info.from, info.to) : '',
        source: source !== null ? markdown.substring(source.from, source.to) : ''
      }
      return astNode
    }
    case 'YAMLFrontmatter': {
      const source = node.getChild('CodeText')
      const astNode: YAMLFrontmatter = {
        type: 'YAMLFrontmatter',
        name: 'YAMLFrontmatter',
        from: node.from,
        to: node.to,
        whitespaceBefore: getWhitespaceBeforeNode(node, markdown),
        source: source !== null ? markdown.substring(source.from, source.to) : ''
      }
      return astNode
    }
    case 'InlineCode': {
      const [ start, end ] = node.getChildren('CodeMark')
      const astNode: InlineCode = {
        type: 'InlineCode',
        name: 'InlineCode',
        from: node.from,
        to: node.to,
        whitespaceBefore: getWhitespaceBeforeNode(node, markdown),
        source: markdown.substring(start.to, end.from)
      }
      return astNode
    }
    case 'Comment':
    case 'CommentBlock': {
      const astNode: Comment = {
        type: 'Comment',
        name: node.name,
        from: node.from,
        to: node.to,
        whitespaceBefore: getWhitespaceBeforeNode(node, markdown),
        value: markdown.slice(node.from + 4, node.to - 3).trim() // <!-- and -->
      }
      return astNode
    }
    case 'Emphasis':
    case 'StrongEmphasis': {
      const astNode: Emphasis = {
        type: 'Emphasis',
        name: 'Emphasis',
        which: node.name === 'Emphasis' ? 'italic' : 'bold',
        from: node.from,
        to: node.to,
        whitespaceBefore: getWhitespaceBeforeNode(node, markdown),
        children: []
      }

      return parseChildren(astNode, node, markdown)
    }
    case 'Superscript': {
      const astNode: Superscript = {
        type: 'Superscript',
        name: 'Superscript',
        from: node.from,
        to: node.to,
        whitespaceBefore: getWhitespaceBeforeNode(node, markdown),
        children: []
      }

      return parseChildren(astNode, node, markdown)
    }
    case 'Subscript': {
      const astNode: Subscript = {
        type: 'Subscript',
        name: 'Subscript',
        from: node.from,
        to: node.to,
        whitespaceBefore: getWhitespaceBeforeNode(node, markdown),
        children: []
      }

      return parseChildren(astNode, node, markdown)
    }
    case 'Table':
      // Tables are somewhat cumbersome to convert, so we outsource it to its own function
      return parseTableNode(node, markdown)
    case 'ZknLink': {
      const content = node.getChild('ZknLinkContent')
      if (content === null) {
        throw new Error('Could not parse node ZknLink: No ZknLinkContent node found within children!')
      }
      const title = node.getChild('ZknLinkTitle') ?? content
      const astNode: ZettelkastenLink = {
        type: 'ZettelkastenLink',
        name: 'ZknLink',
        from: node.from,
        to: node.to,
        whitespaceBefore: getWhitespaceBeforeNode(node, markdown),
        value: markdown.substring(content.from, content.to),
        title: genericTextNode(title.from, title.to, markdown.substring(title.from, title.to))
      }
      return astNode
    }
    case 'ZknTag': {
      const astNode: ZettelkastenTag = {
        type: 'ZettelkastenTag',
        name: 'ZknTag',
        from: node.from,
        to: node.to,
        whitespaceBefore: getWhitespaceBeforeNode(node, markdown),
        value: markdown.substring(node.from + 1, node.to)
      }
      return astNode
    }
    default: {
      const astNode: GenericNode = {
        type: 'Generic',
        name: node.name,
        from: node.from,
        to: node.to,
        whitespaceBefore: getWhitespaceBeforeNode(node, markdown),
        children: []
      }
      return parseChildren(astNode, node, markdown)
    }
  }
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        parseTableNode
 * CVM-Role:        Utility
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     Since table node parsing is very complex as the
 *                  corresponding lezer parser is intentionally "dumb" (likely
 *                  to increase parser speed), parsing tables requires a lot of
 *                  code which is why this function has been externalized.
 *
 * END HEADER
 */

import { type SyntaxNode } from '@lezer/common'
import type { Table, TableRow, TableCell } from '../markdown-ast'
import { getWhitespaceBeforeNode } from './get-whitespace-before-node'
import { parseChildren } from './parse-children'
import { genericTextNode } from './generic-text-node'

/**
 * Parses a SyntaxNode of name "Table"
 *
 * @param   {SyntaxNode}  node      The node to parse
 * @param   {string}      markdown  The original Markdown source
 *
 * @return  {Table}                 The parsed Table AST node
 */
export function parseTableNode (node: SyntaxNode, markdown: string): Table {
  const astNode: Table = {
    type: 'Table',
    name: 'Table',
    from: node.from,
    to: node.to,
    whitespaceBefore: getWhitespaceBeforeNode(node, markdown),
    rows: []
  }

  const header = node.getChildren('TableHeader')
  const rows = node.getChildren('TableRow')

  // The parser cannot reliably extract the table delimiters, but we need
  // those for the column alignment. Thus, we need to see if we can find the
  // header row (pipe tables) or a delimiter row (grid tables) in order to
  // determine the column alignments.
  for (const line of markdown.substring(node.from, node.to).split('\n')) {
    if (!/^[|+:-]+$/.test(line)) {
      continue
    }

    // Gotcha.
    if (line.includes('|')) {
      astNode.tableType = 'pipe'
    } else {
      astNode.tableType = 'grid'
    }

    // The plus indicates a special Pandoc-type of pipe table
    const splitter = line.includes('+') ? '+' : '|'
    astNode.alignment = line.split(splitter)
      // NOTE: |-|-| will result in ['', '-', '-', ''] -> filter out
      .filter(c => c.length > 0)
      .map(c => {
        if (c.startsWith('|')) {
          c = c.substring(1)
        }
        if (c.endsWith('|')) {
          c = c.substring(0, c.length - 1)
        }
        if (c.startsWith(':') && c.endsWith(':')) {
          return 'center'
        } else if (c.endsWith(':')) {
          return 'right'
        } else {
          return 'left'
        }
      })
    break
  } // Else: Couldn't determine either column alignment nor table type

  // Now, transform the rows.
  for (const row of [ ...header, ...rows ]) {
    const rowNode: TableRow = {
      type: 'TableRow',
      name: row.name,
      from: row.from,
      to: row.to,
      whitespaceBefore: '',
      isHeaderOrFooter: row.name === 'TableHeader',
      cells: []
    }

    // NOTE: The Lezer parser intentionally does not emit TableCell nodes for
    // empty cells, so we cannot fully rely on the existence of table cells. See:
    // https://github.com/lezer-parser/markdown/issues/23
    // Thus, we have to manually move through the line one by one. If a
    // TableDelimiter is followed by another TableDelimiter, we know that there
    // is an empty cell in between.
    let next = row.firstChild
    let wasDelim = false
    while (next !== null) {
      if (next.name === 'TableDelimiter' && !wasDelim) {
        wasDelim = true
      } else if (next.name === 'TableDelimiter' && wasDelim) {
        // Last iteration was a TableDelimiter, and now again --> Empty cell
        const prev = next.prevSibling!
        const cellNode: TableCell = {
          type: 'TableCell',
          name: 'TableCell',
          from: prev.to,
          to: next.from,
          whitespaceBefore: '',
          // Has no real children; here we basically just account for any whitespace
          children: [
            genericTextNode(prev.to, next.from, markdown.slice(prev.to, next.from))
          ]
        }
        rowNode.cells.push(cellNode)
        wasDelim = false
      } else if (next.name === 'TableCell') {
        // Functional table cell
        const cellNode: TableCell = {
          type: 'TableCell',
          name: 'TableCell',
          from: next.from,
          to: next.to,
          whitespaceBefore: '',
          children: []
        }
        parseChildren(cellNode, next, markdown)
        rowNode.cells.push(cellNode)
        wasDelim = false
      } else {
        console.warn(`Could not fully parse Table node: Unexpected node "${next.name}" in row.`)
        wasDelim = false
      }
      next = next.nextSibling
    }

    // Special case handling: The Lezer parser unfortunately is a bit sloppy
    // when it comes to grid table parsing and often includes the delimiting
    // pipes between cells as part of the TableCell nodes. Here we account for
    // that and remove that pipe if applicable.
    for (const cellNode of rowNode.cells) {
      if (
        astNode.tableType === 'grid' && cellNode.children.length > 0 &&
        cellNode.children[0].type === 'Text' && cellNode.children[0].value.startsWith('|')
      ) {
        cellNode.children[0].value = cellNode.children[0].value.substring(1)
        cellNode.children[0].from += 1
        cellNode.from += 1
      }
    }
    astNode.rows.push(rowNode)
  }
  return astNode
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        getWhitespaceBeforeNode
 * CVM-Role:        Utility
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     Takes a SyntaxNode and returns any whitespace that preceeds
 *                  the node. This is important since whitespace can be
 *                  significant; especially when building HTML.
 *
 * END HEADER
 */

import { type SyntaxNode } from '@lezer/common'

/**
 * Extracts any amount of whitespace (\t\s\n\r\f\v, etc.) that occurs before
 * this node.
 *
 * @param   {SyntaxNode}  node      The node to extract whitespace for
 * @param   {string}      markdown  The Markdown source to extract the whitespace
 *
 * @return  {string}                The whitespace string
 */
export function getWhitespaceBeforeNode (node: SyntaxNode, markdown: string): string {
  if (node.prevSibling !== null) {
    const sliceBefore = markdown.substring(node.prevSibling.to, node.from)
    const onlyWhitespace = /(\s*)$/m.exec(sliceBefore) // NOTE the "m" flag
    return onlyWhitespace !== null ? onlyWhitespace[1] : ''
  } else if (node.parent !== null) {
    const sliceBefore = markdown.substring(node.parent.from, node.from)
    const onlyWhitespace = /(\s*)$/m.exec(sliceBefore) // NOTE the "m" flag
    return onlyWhitespace !== null ? onlyWhitespace[1] : ''
  } else {
    return ''
  }
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        parseChildren AST module
 * CVM-Role:        Utility
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     Utility function for the AST parser that parses the children
 *                  of a semi-parsed AST node.
 *
 * END HEADER
 */

import type { SyntaxNode } from '@lezer/common'
import { type ASTNode, parseNode, type MDNode } from '../markdown-ast'
import { getWhitespaceBeforeNode } from './get-whitespace-before-node'
import { genericTextNode } from './generic-text-node'

/**
 * This list contains all Node names that do not themselves have any content.
 * These are either purely formatting nodes (such as heading marks or link
 * marks) who can be reconstructed without the verbatim value, as well as larger
 * container nodes (whose contents is represented via their children).
 *
 * @var {string[]}
 */
const EMPTY_NODES = [
  'HeaderMark',
  'CodeMark',
  'EmphasisMark',
  'SuperscriptMark',
  'SubscriptMark',
  'HighlightMark',
  'QuoteMark',
  'ListMark',
  'YAMLFrontmatterStart',
  'YAMLFrontmatterEnd',
  'Document',
  'List',
  'ListItem',
  'PandocAttribute'
]

/**
 * Parses an attribute node (PandocAttribute), according to the Pandoc rules
 * (mostly). cf.: https://pandoc.org/MANUAL.html#extension-attributes
 *
 * @param   {Record<string, string>}  oldAttributes  Attribute nodes are merged.
 * @param   {SyntaxNode}              node           The SyntaxNode
 * @param   {string}                  markdown       The original markdown
 *
 * @return  {Record<string, string>}                 A map of the attributes
 */
function parseAttributeNode (oldAttributes: Record<string, string> = {}, node: SyntaxNode, markdown: string): Record<string, string> {
  if (node.name !== 'PandocAttribute') {
    return oldAttributes
  }

  const rawString: string = markdown.substring(node.from + 1, node.to - 1) // Remove { and }
  const rawAttributes: string[] = rawString.split(/\s+/)
  // General syntax: {#identifier .class .class key=value key=value}
  for (const attribute of rawAttributes) {
    if (attribute.startsWith('.')) {
      // It's a class
      if ('class' in oldAttributes) {
        oldAttributes.class = oldAttributes.class + ' ' + attribute.substring(1)
      } else {
        oldAttributes.class = attribute.substring(1)
      }
    } else if (attribute.startsWith('#') && !('id' in oldAttributes)) {
      // It's an ID, but only the *first* one found counts
      oldAttributes.id = attribute.substring(1)
    } else if (attribute.includes('=')) {
      // It's a key=value attribute. NOTE: Later generic attributes override
      // earlier ones!
      const parts: string[] = attribute.split('=')
      if (parts.length === 2) {
        oldAttributes[parts[0]] = parts[1]
      } // Else: Invalid
    }
  }
  return oldAttributes
}

/**
 * Parses the children of ASTNodes who can have children.
 *
 * @param   {T}           astNode   The AST node that must support children
 * @param   {SyntaxNode}  node      The original Lezer SyntaxNode
 * @param   {string}      markdown  The Markdown source
 *
 * @return  {T}                     Returns the same astNode with children.
 */
export function parseChildren<T extends { children: ASTNode[] } & MDNode> (astNode: T, node: SyntaxNode, markdown: string): T {
  if (node.firstChild === null) {
    if (!EMPTY_NODES.includes(node.name)) {
      const textNode = genericTextNode(node.from, node.to, markdown.substring(node.from, node.to), getWhitespaceBeforeNode(node, markdown))
      astNode.children = [textNode]
    }
    return astNode // We're done
  }

  astNode.children = []

  let currentChild: SyntaxNode|null = node.firstChild
  let currentIndex = node.from
  while (currentChild !== null) {
    // NOTE: We have to account for "gaps" where a node has children that do not
    // completely cover the node's contents. In that case, we have to add text
    // nodes that just contain those strings.
    if (currentChild.from > currentIndex && !EMPTY_NODES.includes(node.name)) {
      const gap = markdown.substring(currentIndex, currentChild.from)
      const onlyWhitespace = /^(\s*)/m.exec(gap)
      const whitespaceBefore = onlyWhitespace !== null ? onlyWhitespace[1] : ''
      const textNode = genericTextNode(
        currentIndex,
        currentChild.from,
        gap.substring(whitespaceBefore.length),
        whitespaceBefore
      )
      astNode.children.push(textNode)
    }
    if (currentChild.name === 'PandocAttribute') {
      // PandocAttribute nodes should never show up in the tree
      // TODO: This assumes that the PandocAttribute should apply to the parent
      // node, but often (e.g., for images) they belong to the previous child!
      // TODO: Check what the *previous* child was, and if it can have attributes
      // Docs: https://pandoc.org/MANUAL.html#extension-attributes
      astNode.attributes = parseAttributeNode(astNode.attributes, currentChild, markdown)
    } else {
      astNode.children.push(parseNode(currentChild, markdown))
    }
    currentIndex = currentChild.to // Must happen before the nextSibling assignment
    currentChild = currentChild.nextSibling
  }

  if (currentIndex < node.to && !EMPTY_NODES.includes(node.name)) {
    // One final text node
    const textNode = genericTextNode(currentIndex, node.to, markdown.substring(currentIndex, node.to))
    astNode.children.push(textNode)
  }

  return astNode
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        md2html
 * CVM-Role:        Utility Function
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     This file contains a Markdown to HTML converter that will
 *                  take in a Markdown source string and convert it to HTML. It
 *                  uses the MainEditor's Markdown parser instance to ensure
 *                  that every element that the editor recognizes is being
 *                  appropriately converted into HTML tags. Any Markdown element
 *                  that comes from the Lezer tree and is not dedicatedly
 *                  handled (read: It has type 'Generic') the formatter will
 *                  create a Span or DIV element (depending on how many children
 *                  the generic has) and supply the node name (from the Lezer
 *                  tree) as a classname, converting the CamelCase to kebab-case.
 *
 * END HEADER
 */

import katex from 'katex'
import 'katex/contrib/mhchem'
import { markdownToAST } from '.'
import { type ASTNode, type GenericNode } from './markdown-ast'
import { type MarkdownParserConfig } from '../markdown-editor/parser/markdown-parser'
import _ from 'underscore'

/**
 * Represents an HTML tag. This is a purposefully shallow representation
 */
interface HTMLTag {
  /**
   * The tag name for the resulting HTML tag
   */
  tagName: string
  /**
   * Self closing are, e.g., <hr>
   */
  selfClosing: boolean
  /**
   * A simple map of attributes (e.g., ['class', 'my-class'])
   */
  attributes: Array<[ string, string ]>
}

export type CitationCallback = (citations: CiteItem[], composite: boolean) => string|undefined

export interface MD2HTMLCallbacks {
  /**
   * Can be used to hook into the image tag generation to alter the image's
   * `src` attribute from the Markdown.
   *
   * @param   {string}  src  The link as it is written in the Markdown source.
   *
   * @return  {string}       Returns whatever should be taken as the `src`
   *                         attribute for the resulting `<img>` tag.
   */
  onImageSrc: (src: string) => string
}

/**
 * This function looks at a GenericNode and returns information regarding the
 * tag that the node should result in.
 *
 * @param   {GenericNode}  node  The input node
 *
 * @return  {HTMLTag}            The HTML tag information
 */
function getTagInfo (node: GenericNode): HTMLTag {
  const ret: HTMLTag = {
    tagName: 'div',
    selfClosing: false,
    attributes: []
  }

  if (node.name === 'HorizontalRule') {
    ret.tagName = 'hr'
    ret.selfClosing = true
  } else if (node.name === 'Paragraph') {
    ret.tagName = 'p'
  } else if (node.name === 'FencedCode' || node.name === 'InlineCode') {
    ret.tagName = 'code'
  } else if (node.children.length === 1) {
    ret.tagName = 'span'
  }

  if ([ 'span', 'div', 'p' ].includes(ret.tagName)) {
    ret.attributes.push([ 'class', node.name ])
  }

  return ret
}

/**
 * Takes a Markdown AST node and turns it to an HTML string
 *
 * @param   {ASTNode}  node         The node
 * @param   {Function} getCitation  The callback for the citations
 * @param   {number}   indent       The indentation for this node
 *
 * @return  {string}                The HTML string
 */
function nodeToHTML (node: ASTNode|ASTNode[], getCitation: CitationCallback, hooks: Partial<MD2HTMLCallbacks>, indent: number = 0): string {
  // Convenience to convert a list of child nodes to HTML
  if (Array.isArray(node)) {
    const body: string[] = []
    for (const child of node) {
      body.push(nodeToHTML(child, getCitation, hooks, indent))
    }
    return body.join('')
  } else if (node.type === 'Generic' && node.name === 'Document') {
    // This ensures there's no outer div class=Document
    return nodeToHTML(node.children, getCitation, hooks, indent)
  } else if (node.type === 'YAMLFrontmatter') {
    return '' // Frontmatters must be removed upon HTML export
  } else if (node.type === 'Citation') {
    const rendered = getCitation(node.parsedCitation.citations, node.parsedCitation.composite)
    return `${node.whitespaceBefore}<span class="citation">${rendered ?? _.escape(node.value)}</span>`
  } else if (node.type === 'Footnote') {
    return `${node.whitespaceBefore}<a class="footnote" href="#fnref:${_.escape(node.label)}">${_.escape(node.label)}</a>`
  } else if (node.type === 'FootnoteRef') {
    return `${node.whitespaceBefore}<div class="footnote-ref"><a name="fnref:${_.escape(node.label)}"></a>${nodeToHTML(node.children, getCitation, hooks, indent)}</div>`
  } else if (node.type === 'Heading') {
    return `${node.whitespaceBefore}<h${node.level}>${_.escape(node.value.value)}</h${node.level}>`
  } else if (node.type === 'Highlight') {
    return `${node.whitespaceBefore}<mark>${nodeToHTML(node.children, getCitation, hooks, indent)}</mark>`
  } else if (node.type === 'Superscript') {
    return `${node.whitespaceBefore}<sup>${nodeToHTML(node.children, getCitation, hooks, indent)}</sup>`
  } else if (node.type === 'Subscript') {
    return `${node.whitespaceBefore}<sub>${nodeToHTML(node.children, getCitation, hooks, indent)}</sub>`
  } else if (node.type === 'Image') {
    const src = hooks.onImageSrc !== undefined ? hooks.onImageSrc(node.url) : node.url
    return `${node.whitespaceBefore}<img src="${src}" alt="${_.escape(node.alt.value)}" title="${node.title?.value ?? _.escape(node.alt.value)}">`
  } else if (node.type === 'Link') {
    return `${node.whitespaceBefore}<a href="${node.url}" title="${node.title?.value ?? _.escape(node.url)}">${_.escape(node.alt.value)}</a>`
  } else if (node.type === 'OrderedList') {
    const startsAt = node.startsAt > 1 ? ` start="${node.startsAt}"` : ''
    return `${node.whitespaceBefore}<ol${startsAt}>\n${nodeToHTML(node.items, getCitation, hooks, indent)}\n</ol>`
  } else if (node.type === 'BulletList') {
    return `${node.whitespaceBefore}<ul>\n${nodeToHTML(node.items, getCitation, hooks, indent)}\n</ul>`
  } else if (node.type === 'ListItem') {
    const task = node.checked !== undefined ? `<input type="checkbox" disabled="disabled" ${node.checked ? 'checked="checked"' : ''}>` : ''
    return `${node.whitespaceBefore}<li>${task}${nodeToHTML(node.children, getCitation, hooks, indent + 1)}</li>`
  } else if (node.type === 'Emphasis') {
    const body = nodeToHTML(node.children, getCitation, hooks, indent)

    switch (node.which) {
      case 'bold':
        return `${node.whitespaceBefore}<strong>${body}</strong>`
      case 'italic':
        return `${node.whitespaceBefore}<em>${body}</em>`
    }
  } else if (node.type === 'Table') {
    const rows: string[] = []
    for (const row of node.rows) {
      const cells: string[] = []
      for (const cell of row.cells) {
        cells.push(nodeToHTML(cell.children, getCitation, hooks, indent))
      }
      const tag = row.isHeaderOrFooter ? 'th' : 'td'
      const content = cells.map(c => `<${tag}>${c}</${tag}>`).join('\n')
      if (row.isHeaderOrFooter) {
        rows.push(`${row.whitespaceBefore}<thead>\n<tr>\n${content}\n</tr>\n</thead>`)
      } else {
        rows.push(`${row.whitespaceBefore}<tr>\n${content}\n</tr>`)
      }
    }
    return `${node.whitespaceBefore}<table>\n${rows.join('\n')}\n</table>`
  } else if (node.type === 'Text') {
    return node.whitespaceBefore + node.value // Plain text
  } else if (node.type === 'FencedCode') {
    if (node.info === '$$') {
      return node.whitespaceBefore + katex.renderToString(node.source)
    } else {
      return `${node.whitespaceBefore}<pre><code class="language-${node.info}">${_.escape(node.source)}</code></pre>`
    }
  } else if (node.type === 'InlineCode') {
    return `${node.whitespaceBefore}<code>${_.escape(node.source)}</code>`
  } else if (node.type === 'Generic') {
    // Generic nodes are differentiated by name. There are a few we can support,
    // but most we wrap in a div.
    const tagInfo = getTagInfo(node)

    if ([ 'div', 'span' ].includes(tagInfo.tagName) && node.children.length === 0) {
      return '' // Simplify the resulting HTML by removing empty elements
    }

    const attr = tagInfo.attributes.length > 0
      ? ' ' + tagInfo.attributes.map(a => `${a[0]}="${a[1]}"`).join(' ')
      : ''

    const open = `${node.whitespaceBefore}<${tagInfo.tagName}${attr}${tagInfo.selfClosing ? '/' : ''}>`
    const close = tagInfo.selfClosing ? '' : `</${tagInfo.tagName}>`
    const body = tagInfo.selfClosing ? '' : nodeToHTML(node.children, getCitation, hooks)
    return `${open}${body}${close}`
  } else if (node.type === 'ZettelkastenLink') {
    // NOTE: We count a ZettelkastenLink's title as a TextNode for various
    // purposes, such as spellchecking it, but it should not contain any syntax
    // which is why we directly access its value here.
    return `${node.whitespaceBefore}[[${node.title.value}]]`
  } else if (node.type === 'ZettelkastenTag') {
    return `${node.whitespaceBefore}#${node.value}`
  }

  return ''
}

/**
 * Takes Markdown source and turns it into a valid HTML fragment. The citeLibrary
 * will be used to resolve citations.
 *
 * @param   {string}           markdown       The Markdown source
 * @param   {Function}         getCitation    The citation callback to use
 * @param   {string}           zknLinkFormat  (Optional) The Wikilink format
 * @param   {MD2HTMLCallbacks} hooks          Any hooks that can be used to programmatically alter the produced HTML
 *
 * @return  {string}                   The resulting HTML
 */
export function md2html (markdown: string, getCitation: CitationCallback, zknLinkFormat: 'link|title'|'title|link' = 'link|title', hooks?: Partial<MD2HTMLCallbacks>): string {
  const config: MarkdownParserConfig = {
    zknLinkParserConfig: { format: zknLinkFormat }
  }
  const ast = markdownToAST(markdown, undefined, config)
  return nodeToHTML(ast, getCitation, hooks ?? {})
}

/**
 * BEGIN HEADER
 *
 * Contains:        Utility function
 * CVM-Role:        <none>
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     This file contains a utility function to count words.
 *
 * END HEADER
 */

import type { ASTNode } from '@common/modules/markdown-utils/markdown-ast'
import { extractTextnodes } from '@common/modules/markdown-utils'

const interpunctionRE = /^[-.:;,'%/\\_!?()[\]{}]+$/

/**
 * Takes an AST, extracts the text nodes and parses them into a pre-cleaned list
 * of words within the document.
 *
 * @param   {ASTNode}   ast   The AST
 * @param   {number}    from  Optional. If given, this function omits any text
 *                            node before this position
 * @param   {number}    to    Optional. If given, this function omits any text
 *                            node after this position.
 *
 * @return  {string[]}        The list of words
 */
function getCleanedWords (ast: ASTNode, from = 0, to?: number): string[] {
  let textNodes = extractTextnodes(ast)
  if (from > 0) {
    textNodes = textNodes.filter(node => node.from >= from || node.to >= from)
    textNodes.map(node => {
      if (node.from < from) {
        node.value = node.value.substring(from - node.from)
      }
      return undefined
    })
  }
  if (to !== undefined) {
    textNodes = textNodes.filter(node => node.to <= to || node.from <= to)
    textNodes.map(node => {
      if (node.to > to) {
        node.value = node.value.substring(0, node.value.length - (node.to - to))
      }
      return undefined
    })
  }

  const plainText = textNodes.map(node => node.value)
  return plainText
    .join(' ')
    .split(/[\s ]+/)
    .filter(w => w.trim() !== '')
    .filter(word => !interpunctionRE.test(word))
}

/**
 * Returns an accurate word count based on a parsed AST.
 *
 * @param   {ASTNode}  ast   The parsed AST to use for counting
 * @param   {number}   from  Optional. If given, this function omits any text
 *                           node before this position
 * @param   {number}   to    Optional. If given, this function omits any text
 *                           node after this position.
 *
 * @return  {number}         The number of words in the file.
 */
export function countWords (ast: ASTNode, from = 0, to?: number): number {
  return getCleanedWords(ast, from, to).length
}

/**
 * Returns an accurate character count (without spaces) based on a parsed AST.
 *
 * @param   {ASTNode}  ast   The parsed AST to use for counting
 * @param   {number}   from  Optional. If given, this function omits any text
 *                           node before this position
 * @param   {number}   to    Optional. If given, this function omits any text
 *                           node after this position.
 *
 * @return  {number}         The number of characters in the file
 */
export function countChars (ast: ASTNode, from = 0, to?: number): number {
  return getCleanedWords(ast, from, to)
    .map(w => w.length)
    .reduce((prev, cur) => prev + cur, 0)
}

/**
 * Counts both words and characters, and characters without spaces.
 *
 * @param   {ASTNode}         ast   The parsed AST to use for counting
 * @param   {number}          from  Optional. If given, this function omits any
 *                                  text node before this position
 * @param   {number}          to    Optional. If given, this function omits any
 *                                  text node after this position.
 *
 * @return  {{words, chars}}        Word and character counts
 */
export function countAll (ast: ASTNode, from = 0, to?: number): { words: number, chars: number } {
  const words = getCleanedWords(ast, from, to)
  return {
    words: words.length,
    chars: words.map(w => w.length).reduce((prev, cur) => prev + cur, 0)
  }
}

// This file implements a document tree structure to accustom split-views within
// document windows. Inspiration taken from https://www.warp.dev/blog/using-tree-data-structures-to-implement-terminal-split-panes-more-fun-than-it-sounds

export { DocumentTree } from './document-tree'
export { DTLeaf } from './document-tree-leaf'
export { DTBranch } from './document-tree-branch'

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        DocumentTree
 * CVM-Role:        Model
 * Maintainer:      Hendrik Ery
 * License:         GNU GPL v3
 *
 * Description:     This file contains the DocumentTree class. It represents the
 *                  editors within the main windows. There is one DocumentTree
 *                  per main window open, and these trees are managed by the
 *                  documents provider. The trees themselves support any
 *                  operation that should be expected from them, such as
 *                  splitting leafs horizontally and vertically, and moving
 *                  stuff around.
 *
 * END HEADER
 */

import type { LeafNodeJSON, BranchNodeJSON } from '@dts/common/documents'
import { DTBranch } from './document-tree-branch'
import { DTLeaf } from './document-tree-leaf'

/**
 * This is the root instance of a document tree. It can contain a single node
 * which is either a leaf, if the window does not contain any splits, or a
 * branch.
 */
export class DocumentTree {
  /**
   * The root node of the tree
   *
   * @var {DTBranch|DTLeaf}
   */
  private _node: DTBranch|DTLeaf

  /**
   * Creates a new document tree (i.e. a new Window)
   *
   * @return  {DocumentTree}  The new tree
   */
  constructor () {
    this._node = new DTLeaf(this)
  }

  /**
   * Sets the root node for the tree
   *
   * @param  {DTBranch|DTLeaf}  data  The new node
   */
  public set node (data: DTBranch|DTLeaf) {
    this._node = data
  }

  /**
   * Returns the root node for the tree
   *
   * @return  {DTBranch|DTLeaf}  The root node
   */
  public get node (): DTBranch|DTLeaf {
    return this._node
  }

  /**
   * Finds the leaf with the given ID
   *
   * @param   {string}  id  The leaf's ID
   *
   * @return  {DTLeaf}      The leaf, or undefined
   */
  public findLeaf (id: string): DTLeaf|undefined {
    if (this._node === undefined) {
      return undefined
    }

    return this._node.findLeaf(id)
  }

  /**
   * Finds the branch with the given ID
   *
   * @param   {string}    id  The branch's ID
   *
   * @return  {DTBranch}      The branch, or undefined
   */
  public findBranch (id: string): DTBranch|undefined {
    if (this._node === undefined || this._node instanceof DTLeaf) {
      return undefined
    } else {
      return this._node.findBranch(id)
    }
  }

  /**
   * Returns a list of all leafs in the tree
   *
   * @return  {DTLeaf[]}  The leaf list
   */
  public getAllLeafs (): DTLeaf[] {
    if (this._node instanceof DTLeaf) {
      return [this._node]
    } else {
      return this._node.getAllLeafs()
    }
  }

  /**
   * Returns the direction of the root branch, if the tree has a root branch.
   * Undefined, if the tree has only a leaf.
   *
   * @return  {string|undefined}  The direction, or undefined
   */
  public get direction (): 'horizontal'|'vertical'|undefined {
    if (this._node instanceof DTLeaf) {
      return undefined
    } else {
      return this._node.direction
    }
  }

  /**
   * Removes the given node from the list of child nodes
   *
   * @param   {DTLeaf|DTBranch}  node  The node to remove
   */
  public removeNode (node: DTLeaf|DTBranch): void {
    if (this._node === node) {
      const newLeaf = new DTLeaf(this)
      this._node = newLeaf
    }
  }

  /**
   * Revitalizes a document tree from JSON data
   *
   * @param   {any}           data  The data (already de-serialized from string)
   *
   * @return  {DocumentTree}        The tree
   */
  static fromJSON (data: any): DocumentTree {
    if (Array.isArray(data)) {
      throw new Error('Could not instantiate tree: Data was an array')
    }

    if (data.type === undefined) {
      throw new Error('Could not instantiate tree: Data missing required property "type"')
    }

    if (![ 'leaf', 'branch' ].includes(data.type)) {
      throw new Error(`Could not instantiate tree: data.type contained unrecognized value ${data.type as string}`)
    }

    const newTree = new DocumentTree()

    if (data.type === 'leaf') {
      newTree.node = DTLeaf.fromJSON(newTree, data)
    } else if (data.type === 'branch') {
      newTree.node = DTBranch.fromJSON(newTree, data)
    }

    return newTree
  }

  /**
   * Returns a JSON representation of this tree that can be stringified
   *
   * @return  {any}     The tree as JSON
   */
  public toJSON (): LeafNodeJSON|BranchNodeJSON {
    return this._node.toJSON()
  }
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        DocumentTreeBranch
 * CVM-Role:        Model
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     This class represents a branch of a document tree. A
 *                  DTBranch can contain a theoretically unlimited number of
 *                  branches and leafs, in either horizontal or vertical
 *                  direction. A branch is used to split up the various editors
 *                  within a main editor window. They manage anything required
 *                  to customize and modify those branches, such as the relative
 *                  sizes of all of its children.
 *
 * END HEADER
 */

import type { BranchNodeJSON } from '@dts/common/documents'
import { DocumentTree } from './document-tree'
import { DTLeaf } from './document-tree-leaf'
import { v4 as uuid4 } from 'uuid'

export class DTBranch {
  private readonly _nodes: Array<DTLeaf|DTBranch>
  private readonly _direction: 'horizontal'|'vertical'
  private _parent: DocumentTree|DTBranch
  private _sizes: number[]
  private readonly _id: string

  /**
   * Creates a new Branch object. By default it will be filled with no nodes.
   *
   * @param   {DocumentTree|DTBranch}  parent    The parent for this branch
   * @param   {string}                 dir       The direction, can be horizontal or vertical
   * @param   {string}                 id        An optional ID, may be changed to ensure uniqueness
   */
  constructor (parent: DocumentTree|DTBranch, dir: 'horizontal'|'vertical', id: string = uuid4()) {
    this._nodes = []
    this._parent = parent
    this._direction = dir
    this._sizes = []

    if (typeof id !== 'string') {
      id = uuid4()
    }

    // Ensure a unique ID
    let root = parent
    while (!(root instanceof DocumentTree)) {
      root = root.parent
    }

    while (root.findBranch(id) !== undefined) {
      id = uuid4()
    }

    this._id = id
  }

  /**
   * Returns the readonly ID for this branch
   *
   * @return  {string}  The ID
   */
  public get id (): string {
    return this._id
  }

  /**
   * Returns a list of all child nodes
   *
   * @return  {Array<DTLeaf|DTBranch>}  The child node list
   */
  public get nodes (): Array<DTLeaf|DTBranch> {
    return this._nodes
  }

  /**
   * Returns the sizes currently belonging to this branch
   *
   * @return  {number[]}  The size array
   */
  public get sizes (): number[] {
    return this._sizes
  }

  /**
   * Provides a new set of sizes for the node. This setter will ensure the sizes
   * are sane afterwards.
   *
   * @param   {number[]}  sizes  The new size set
   */
  public set sizes (sizes: number[]) {
    this._sizes = sizes
    this.ensureProperSizes()
  }

  /**
   * Returns the current parent of this branch
   *
   * @return  {DTBranch|DocumentTree}  The parent
   */
  public get parent (): DTBranch|DocumentTree {
    return this._parent
  }

  /**
   * Exchanges this branch's parent
   *
   * @param   {DTBranch|DocumentTree}  parent  The new parent
   */
  public set parent (parent: DTBranch|DocumentTree) {
    this._parent = parent
  }

  /**
   * Returns a list of all leafs in the tree
   *
   * @return  {DTLeaf[]}  The leaf list
   */
  public getAllLeafs (): DTLeaf[] {
    let ret: DTLeaf[] = []

    for (const node of this._nodes) {
      if (node instanceof DTLeaf) {
        ret.push(node)
      } else {
        ret = ret.concat(node.getAllLeafs())
      }
    }

    return ret
  }

  /**
   * Adds the provided node into the list of children nodes
   *
   * @param   {DTLeaf|DTBranch}  content        The node to be added
   * @param   {DTLeaf|DTBranch}  referenceNode  An optional reference node
   * @param   {before|after}     insertion      Where to insert content based on
   *                                            referenceNode, defaults to after
   */
  public addNode (content: DTLeaf|DTBranch, referenceNode?: DTLeaf|DTBranch, insertion?: 'before'|'after'): void {
    let idx = -1
    if (referenceNode !== undefined) {
      idx = this._nodes.indexOf(referenceNode)
    }

    if (idx > -1) {
      if (insertion === 'before') {
        this._nodes.splice(idx, 0, content)
      } else {
        this._nodes.splice(idx + 1, 0, content)
      }
    } else {
      this._nodes.push(content)
    }
    this.ensureProperSizes()
  }

  /**
   * Removes the given node from the list of child nodes
   *
   * @param   {DTLeaf|DTBranch}  node  The node to remove
   */
  public removeNode (node: DTLeaf|DTBranch): void {
    const idx = this._nodes.indexOf(node)
    if (idx === -1) {
      return
    }

    this._nodes.splice(idx, 1)

    // If we're now left with just a single leaf, remove this branch and
    // attach the remaining node itself to the parent.
    if (this._nodes.length === 1 && this._nodes[0] instanceof DTLeaf) {
      this._nodes[0].parent = this.parent
      if (this.parent instanceof DocumentTree) {
        this.parent.node = this._nodes[0]
      } else {
        this.parent.addNode(this._nodes[0], this, 'after')
        this.parent.removeNode(this)
      }
    } else if (this._nodes.length === 0) {
      // Similarly, if we've just dropped the last node, we can just remove
      // this thing from its parent.
      this.parent.removeNode(this)
    }

    this.ensureProperSizes()
  }

  /**
   * Finds the leaf with the given ID
   *
   * @param   {string}  id  The leaf's ID
   *
   * @return  {DTLeaf}      The leaf, or undefined
   */
  public findLeaf (id: string): DTLeaf|undefined {
    for (const node of this._nodes) {
      if (node instanceof DTLeaf && node.id === id) {
        return node
      } else if (node instanceof DTBranch) {
        const found = node.findLeaf(id)
        if (found !== undefined) {
          return found
        }
      }
    }
  }

  /**
   * Finds the branch with the given ID
   *
   * @param   {string}    id  The branch's ID
   *
   * @return  {DTBranch}      The branch, or undefined
   */
  public findBranch (id: string): DTBranch|undefined {
    if (id === this._id) {
      return this
    }

    for (const node of this._nodes) {
      if (node instanceof DTBranch) {
        const found = node.findBranch(id)
        if (found !== undefined) {
          return found
        }
      }
    }
  }

  /**
   * Returns the direction for this branch
   *
   * @return  {string}  The direction
   */
  public get direction (): 'horizontal'|'vertical' {
    return this._direction
  }

  /**
   * A utility function that checks whether the size values are all sane, and,
   * if not, recomputes them
   */
  private ensureProperSizes (): void {
    let hasProperSizes = true

    // The size array must be of the same length as our nodes
    if (this._nodes.length !== this._sizes.length) {
      hasProperSizes = false
    }

    // Also, all of this must sum up to 100 (percent)
    const totalSize = this._sizes.reduce((acc, val) => acc + val, 0)
    if (Math.round(totalSize) !== 100) {
      hasProperSizes = false
    }

    if (hasProperSizes) {
      return // All good
    }

    // Reset the sizes
    const size = 100 / this._nodes.length
    this._sizes = []
    for (let i = 0; i < this._nodes.length; i++) {
      this._sizes.push(size)
    }
  }

  /**
   * Create a new Branch from the given JSON data
   *
   * @param   {DocumentTree|DTBranch}  parent    The parent node
   * @param   {any}                    nodeData  The JSON data to fill the branch with
   *
   * @return  {Promise<DTBranch>}                 The new branch
   */
  static fromJSON (parent: DocumentTree|DTBranch, nodeData: any): DTBranch {
    if (typeof nodeData !== 'object') {
      throw new Error('Could not instantiate DTBranch: Provided JSON was not an object.')
    }

    const { direction, id, nodes, sizes } = nodeData

    if (typeof direction !== 'string' || (direction !== 'horizontal' && direction !== 'vertical')) {
      throw new Error(`Could not instantiate DTBranch: Invalid split direction: ${direction}`)
    }

    if (typeof id !== 'string') {
      throw new Error(`Could not instantiate DTBranch: ID was invalid: ${String(id)}`)
    }

    if (!Array.isArray(nodes)) {
      throw new Error(`Could not instantiate DTBranch: Nodes was not an array: ${typeof nodes}`)
    }

    // NOTE: After this point, we don't throw any more errors, since the branch
    // can be successfully instantiated. If any of the sub-nodes experience an
    // error, this will propagate through this function to the caller.
    const newBranch = new DTBranch(parent, direction, id)

    for (const subNode of nodes) {
      if (subNode.type === 'leaf') {
        newBranch.addNode(DTLeaf.fromJSON(newBranch, subNode))
      } else if (subNode.type === 'branch') {
        newBranch.addNode(DTBranch.fromJSON(newBranch, subNode))
      }
    }

    // We don't have to throw an error if the sizes are wrong, since then the
    // leafs/branches are simply equally large.
    if (Array.isArray(sizes) && sizes.every(x => typeof x === 'number') && sizes.length === nodes.length) {
      newBranch.sizes = nodeData.sizes
    }

    return newBranch
  }

  /**
   * Creates a JSON serializable representation for this branch
   *
   * @return  {BranchNodeJSON}  The JSON data
   */
  public toJSON (): BranchNodeJSON {
    const json: BranchNodeJSON = {
      type: 'branch',
      id: this._id,
      direction: this._direction,
      sizes: this._sizes,
      nodes: []
    }

    for (const node of this._nodes) {
      json.nodes.push(node.toJSON())
    }

    return json
  }
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        DocumentTreeLeaf
 * CVM-Role:        Model
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     Document Tree Leafs represent a single document pane within
 *                  a window. Formally, they are a wrapper around a tab manager.
 *                  We need to separate the tab manager from the leaf, since the
 *                  leaf needs to handle splitting itself up into a branch with
 *                  two leaves.
 *
 * END HEADER
 */

import isFile from '@common/util/is-file'
import type { OpenDocument, LeafNodeJSON } from '@dts/common/documents'
import { v4 as uuid4 } from 'uuid'
import { DocumentTree } from './document-tree'
import { DTBranch } from './document-tree-branch'
import { TabManager } from './tab-manager'

export class DTLeaf {
  // This is info concerning the tree structure
  private readonly _id: string
  private _parent: DTBranch|DocumentTree
  // The tab manager actually manages the data
  private readonly _tabManager: TabManager

  /**
   * Creates a new empty leaf.
   *
   * @param  {DTBranch|DocumentTree}  parent  The parent for this leaf
   * @param  {string}                 id      An optional ID, may be changed to ensure uniqueness
   */
  constructor (
    parent: DTBranch|DocumentTree,
    id: string = uuid4()
  ) {
    this._parent = parent
    this._tabManager = new TabManager()

    if (typeof id !== 'string') {
      id = uuid4()
    }

    // Ensure a unique ID
    let root = parent
    while (!(root instanceof DocumentTree)) {
      root = root.parent
    }

    while (root.findLeaf(id) !== undefined) {
      id = uuid4()
    }

    this._id = id
  }

  /**
   * Returns the tab manager associated with this leaf
   *
   * @return  {TabManager}  The tab manager instance
   */
  public get tabMan (): TabManager {
    return this._tabManager
  }

  /**
   * Returns the readonly ID for this leaf
   *
   * @return  {string}  The ID
   */
  public get id (): string {
    return this._id
  }

  /**
   * Returns this leaf's parent
   *
   * @return  {DTBranch|DocumentTree}  The parent
   */
  public get parent (): DTBranch|DocumentTree {
    return this._parent
  }

  /**
   * Sets the parent for this leaf
   *
   * @param   {DTBranch|DocumentTree}  parent  The new parent
   */
  public set parent (parent: DTBranch|DocumentTree) {
    this._parent = parent
  }

  /**
   * Directs the leaf to split itself. This can either mean to insert a new leaf
   * into its parent's child nodes, or to add the leaf to a new branch and
   * replace itself in the tree with that new branch.
   *
   * @param   {string}  direction  The direction in which to split
   * @param   {string}  insertion  Where to split (before the leaf, or after)
   *
   * @return  {DTLeaf}             Returns the newly created leaf
   */
  public split (direction: 'horizontal'|'vertical', insertion: 'before'|'after'): DTLeaf {
    if (this._parent instanceof DocumentTree) {
      console.log('Replacing this leaf with a new branch as the tree node')
      const newBranch = new DTBranch(this._parent, direction)
      const newLeaf = new DTLeaf(newBranch)

      if (insertion === 'before') {
        newBranch.addNode(newLeaf)
        newBranch.addNode(this)
      } else {
        newBranch.addNode(this)
        newBranch.addNode(newLeaf)
      }

      this._parent.node = newBranch
      this._parent = newBranch
      return newLeaf
    } else if (this._parent.direction === direction) {
      // Same direction, and we're already in a split -> add a new node
      const newLeaf = new DTLeaf(this._parent)
      this._parent.addNode(newLeaf, this, insertion)
      return newLeaf
    } else {
      // Different direction -> replace this node with a new branch
      const newBranch = new DTBranch(this._parent, direction)
      const newLeaf = new DTLeaf(newBranch)

      if (insertion === 'before') {
        newBranch.addNode(newLeaf)
        newBranch.addNode(this)
      } else {
        newBranch.addNode(this)
        newBranch.addNode(newLeaf)
      }

      this._parent.addNode(newBranch, this, 'after')
      this._parent.removeNode(this)

      this._parent = newBranch
      return newLeaf
    }
  }

  /**
   * Returns this if the ID is this leaf's ID, else undefined.
   *
   * @param   {string}  id  The leaf ID
   *
   * @return  {DTLeaf}      This, or undefined
   */
  public findLeaf (id: string): this|undefined {
    if (id === this._id) {
      return this
    }
  }

  /**
   * Creates a new leaf based on the nodeData provided
   *
   * @param   {DocumentTree|DTBranch}  parent    The parent for the leaf
   * @param   {any}                    nodeData  The node data
   *
   * @return  {Promise<DTLeaf>}                  Resolves with the new leaf
   */
  static fromJSON (parent: DocumentTree|DTBranch, nodeData: any): DTLeaf {
    if (typeof nodeData !== 'object') {
      throw new Error('Could not instantiate DTLeaf: Provided JSON was not an object.')
    }

    const { id, openFiles } = nodeData

    if (typeof id !== 'string') {
      throw new Error(`Could not instantiate DTLeaf: ID was invalid: ${String(id)}`)
    }

    if (!Array.isArray(openFiles)) {
      throw new Error(`Could not instantiate DTLeaf: openFiles was not an array: ${typeof openFiles}`)
    }

    // NOTE: After this point, we don't throw any more errors, since the leaf
    // can be successfully instantiated. The only thing that may miss are the
    // open documents.
    const leaf = new DTLeaf(parent, id)
    for (const file of openFiles) {
      if (typeof file !== 'object') {
        continue // Invalid entry
      }

      const { path, pinned } = file

      if (typeof path !== 'string' || typeof pinned !== 'boolean' || !isFile(path)) {
        continue // Invalid entry
      }

      const success = leaf.tabMan.openFile(path, false)
      if (success) {
        leaf.tabMan.setPinnedStatus(path, pinned)
      } else {
        // Invalid entry
      }
    }

    // Revitalize the active File pointer
    const activeFile = leaf.tabMan.openFiles.find(e => e.path === nodeData.activeFile?.path) ?? null
    leaf.tabMan.activeFile = activeFile

    // If the last active file can't be restored, make the first one of this
    // leaf active so that the editor shows something.
    if (leaf.tabMan.activeFile === null && leaf.tabMan.openFiles.length > 0) {
      leaf.tabMan.activeFile = leaf.tabMan.openFiles[0]
    }

    return leaf
  }

  /**
   * Returns a JSON serializable representation for this leaf
   *
   * @return  {LeafNodeJSON}  The JSON data
   */
  public toJSON (): LeafNodeJSON {
    return {
      type: 'leaf',
      id: this._id,
      ...this._tabManager.toJSON()
    }
  }
}

