import { lstatSync } from 'fs'

/**
 * Checks if a given path is a valid file
 *
 * @param  {string}  p The path to check
 *
 * @return {boolean}   True, if it is a valid path + file, and false if not
 */
export default function isFile (p: string): boolean {
  try {
    let s = lstatSync(p)
    return s.isFile()
  } catch (err) {
    return false
  }
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        TabManager
 * CVM-Role:        Model
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     The tab manager manages the open documents for a single
 *                  editor pane. Tab managers aren't instantiated standalone but
 *                  are meant to be contained within a document tree leaf.
 *
 * END HEADER
 */

import type { OpenDocument } from '@dts/common/documents'

export interface TabManagerJSON {
  openFiles: OpenDocument[]
  activeFile: OpenDocument|null
}

export class TabManager {
  private readonly _openFiles: OpenDocument[]
  private _activeFile: OpenDocument|null
  private readonly _sessionHistory: string[]
  private _sessionPointer: number

  constructor () {
    this._openFiles = []
    this._activeFile = null
    this._sessionHistory = []
    this._sessionPointer = -1
  }

  // GETTERS AND SETTERS

  /**
   * Returns the list of currently opened files in this tab manager
   *
   * @return  {OpenDocument[]}  The currently open documents
   */
  public get openFiles (): OpenDocument[] {
    return this._openFiles
  }

  /**
   * Sets the active file to the one given; can either be an OpenDocument or
   * a file path. Provide null to unset
   *
   * @param  {OpenDocument|string|null}  file  The new active file
   */
  public set activeFile (file: OpenDocument|string|null) {
    if (typeof file === 'string') {
      const doc = this._openFiles.find(doc => doc.path === file)
      if (doc === undefined) {
        return
      }
      file = doc
    }

    this._activeFile = file
  }

  /**
   * Returns the current active file
   *
   * @return  {OpenDocument|null}  The active file or null
   */
  public get activeFile (): OpenDocument|null {
    return this._activeFile
  }

  // PUBLIC METHODS

  /**
   * Sorts the openFiles according to pathArray.
   *
   * @param  {string[]}  pathArray  An array with absolute paths to sort with
   *
   * @return {boolean}              The new sorting
   */
  public sortOpenFiles (pathArray: string[]): boolean {
    // Only sort if something changed
    if (pathArray.length === this._openFiles.length) {
      let somethingChanged = false
      for (let i = 0; i < pathArray.length; i++) {
        if (this._openFiles[i].path !== pathArray[i]) {
          somethingChanged = true
          break
        }
      }

      if (!somethingChanged) {
        return false
      }
    }

    // Simply re-sort based on the new paths
    this._openFiles.sort((a, b) => {
      return pathArray.indexOf(a.path) - pathArray.indexOf(b.path)
    })

    this.movePinnedTabsLeft()

    return true
  }

  /**
   * This function (re)sorts the open files solely based on their pinned status
   */
  private movePinnedTabsLeft (): void {
    // Also make sure that pinned tabs are all grouped to the left before sync
    this._openFiles.sort((a, b) => {
      if (a.pinned && !b.pinned) return -1
      if (!a.pinned && b.pinned) return 1
      return 0
    })
  }

  /**
   * Opens a file within this tab manager.
   *
   * @param   {string}   filePath       The absolute file path
   * @param   {boolean}  modifyHistory  Optional. Only used internally.
   *
   * @return  {Promise<boolean>}        True upon successful opening
   */
  public openFile (filePath: string, modifyHistory?: boolean): boolean {
    if (this.activeFile?.path === filePath) {
      return false
    }
    const openFile = this._openFiles.find(file => file.path === filePath)

    // Remove the file from the session history if applicable
    if (modifyHistory !== false) {
      const sessionIndex = this._sessionHistory.indexOf(filePath)
      if (sessionIndex > -1) {
        this._sessionHistory.splice(sessionIndex, 1)
      }
    }

    // If the file is already open, we just set it as the active one and be done
    // with it, no further action needed
    if (openFile !== undefined) {
      if (modifyHistory !== false) {
        this._sessionHistory.push(filePath)
        this._sessionPointer = this._sessionHistory.length - 1
      }
      this.activeFile = openFile
      return true
    }

    const file: OpenDocument = { path: filePath, pinned: false }

    if (this._activeFile !== null) {
      // ... behind our active file
      const idx = this._openFiles.indexOf(this._activeFile)
      this._openFiles.splice(idx + 1, 0, file)
    } else {
      // ... or at the end
      this._openFiles.push(file)
    }

    // Update all required states. Especially make sure to re-sort this to
    // ensure the new file (unpinned) doesn't end up in between several pinned
    // files.
    this.sortOpenFiles(this._openFiles.map(d => d.path))
    this.movePinnedTabsLeft()

    this.activeFile = file

    if (modifyHistory !== false) {
      this._sessionHistory.push(filePath)
      this._sessionPointer = this._sessionHistory.length - 1
    }

    return true
  }

  /**
   * Closes the given file if it's in fact open. This function deals with every
   * potential problem such as retrieving user consent to closing the file if it
   * is modified.
   *
   * @param   {MDFileDescriptor|CodeFileDescriptor}  file  The file to be closed
   *
   * @return  {boolean}                                    Whether or not the file was closed
   */
  public closeFile (file: OpenDocument|string): boolean {
    if (typeof file === 'string') {
      const doc = this._openFiles.find(doc => doc.path === file)
      if (doc !== undefined) {
        file = doc
      } else {
        return false
      }
    }
    if (!this._openFiles.includes(file)) {
      return false // All good, we didn't even have to close the file.
    }

    if (file.pinned) {
      // TODO this._app.log.warning(`[Document Provider] Refusing to close pinned file ${file.path}`)
      return false
    }

    // Retrieve the index of the active file and whether it's an active file
    const activeFileIdx = (this._activeFile === null) ? -1 : this._openFiles.indexOf(this._activeFile)
    const isActive = this._activeFile === file

    // Then remove the file from the list of open files
    this._openFiles.splice(this._openFiles.indexOf(file), 1)

    // Now, if we just closed the active file, we need to make another file
    // active, or none, if there are no more open files active.
    if (!isActive) {
      return true
    } else {
      this.activeFile = null
    }

    if (this._openFiles.length > 0 && activeFileIdx > 0) {
      this.activeFile = this._openFiles[activeFileIdx - 1]
    } else if (this._openFiles.length > 0 && activeFileIdx === 0) {
      this.activeFile = this._openFiles[0]
    }

    return true
  }

  /**
   * This function is a convenience when the path of a file has changed without
   * the file being deleted or otherwise removed from the app. NOTE that you
   * still have to emit any events to notify the editors of this change.
   *
   * @param   {string}  oldPath  The old path
   * @param   {string}  newPath  The new path
   *
   * @return  {boolean}          False if the file was not open here
   */
  public replaceFilePath (oldPath: string, newPath: string): boolean {
    const file = this.openFiles.find(doc => doc.path === oldPath)
    if (file === undefined) {
      console.log(`Didnt find file for path ${oldPath} -- nothing has changed.`, this.openFiles.map(doc => doc.path))
      return false
    }

    file.path = newPath
    if (this.activeFile?.path === oldPath) {
      this.activeFile = file
    }

    return true
  }

  /**
   * Goes back in the session history and opens the previous file
   */
  public back (): void {
    this._moveThroughHistory(-1)
  }

  /**
   * Goes forward in the session history and opens the next file
   */
  public forward (): void {
    this._moveThroughHistory(1)
  }

  /**
   * Moves through history using the specified direction
   *
   * @param   {number}  direction  The direction to take. Negative = back, positive = forward
   */
  private _moveThroughHistory (direction: number): void {
    // Always make sure the session pointer is valid
    if (this._sessionPointer < 0 || this._sessionPointer > this._sessionHistory.length - 1) {
      this._sessionPointer = this._sessionHistory.length - 1
    }

    const targetIndex = this._sessionPointer + direction

    if (targetIndex > this._sessionHistory.length - 1 || targetIndex < 0) {
      return console.log('Out of bounds') // Cannot move: Out of bounds
    }

    // Move the pointer
    this._sessionPointer = targetIndex
    const pathToOpen = this._sessionHistory[this._sessionPointer]

    // Open that file, but tell the opener explicitly not to modify the state
    this.openFile(pathToOpen, false)
  }

  /**
   * Sets the pinned status for the given file.
   *
   * @param   {string}   filePath        The absolute path to the file
   * @param   {boolean}  shouldBePinned  Whether the file should be pinned.
   */
  public setPinnedStatus (filePath: string, shouldBePinned: boolean): void {
    const idx = this._openFiles.findIndex(doc => doc.path === filePath)
    if (idx > -1) {
      this._openFiles[idx].pinned = shouldBePinned
      this.movePinnedTabsLeft()
    }
  }

  // API METHODS

  /**
   * Returns a JSON serializable representation of the tab manager instance
   *
   * @return  {TabManagerJSON}     The JSON data
   */
  public toJSON (): TabManagerJSON {
    return {
      openFiles: this._openFiles,
      activeFile: this._activeFile
    }
  }
}

/**
 * BEGIN HEADER
 *
 * Contains:        Utility function
 * CVM-Role:        <none>
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     This file contains a utility function to extract
 *                  BibTex attachment entries from such a file.
 *
 * END HEADER
 */

import path from 'path'
import { bibtex } from 'astrocite'
import { type BracedComment } from 'astrocite-bibtex'
import pdfSorter from '@common/util/sort-by-pdf'
import type LogProvider from '@providers/log'

const AstrociteAST = bibtex.AST

type BibTexAttachments = Record<string, string[]|false>

/**
 * Returns a dictionary in the form citeKey: Array(files)
 * @param {String} fileContents The contents of a valid BibTex file
 * @param {String} baseDir The base directory to use in case the links are relative.
 * @return {Object} Returns a dictionary containing all extracted files
 */
export default function extractBibtexAttachments (
  fileContents: string,
  baseDir: string,
  logger?: LogProvider
): BibTexAttachments {
  const ast = AstrociteAST.parse(fileContents)
  // Return value will be a fast-access dictionary
  const files = Object.create(null)

  // First we search for the jabref comments containing the files' root directories
  const comments = ast.children.filter(item => item.kind === 'BracedComment') as BracedComment[]
  // The format of the value field is 'jabref-meta: fileDirectory*:<path>;'
  const jabrefComments = comments.filter(item => item.value.startsWith('jabref-meta:'))
  for (const entry of jabrefComments) {
    const value = entry.value.split(':').map(e => e.trim())
    if (value[1].startsWith('fileDirectory')) {
      baseDir = value[2].replace(/;/g, '')
      logger?.info(`[extractBibtexAttachments] Found a fileDirectory, overwriting baseDir: ${baseDir}`)
      break
    }
  }

  // Now let's see what entries have files attached.
  // Such attributes are stored in properties within the entry.
  for (const entry of ast.children) {
    if (entry.kind !== 'Entry') {
      continue
    }

    for (const property of entry.properties) {
      if (property.key === 'file') {
        const firstValue = property.value[0]
        if (firstValue.kind !== 'String' && firstValue.kind !== 'Text') {
          continue
        }

        // The file entry by JabRef is saved as description:path:type
        // Multiple entries are delimited with ;
        // Reference: https://github.com/JabRef/jabref/blob/93f47c9069d01247375cabbe6e1328f0a477472b/src/main/java/org/jabref/gui/filelist/FileListEntry.java#L46
        let f = firstValue.value.split(';')
        f = f.map(elem => {
          // Extract the file paths
          if (elem.includes(':')) {
            return elem.split(':')[1]
          } else {
            return elem
          }
        })
        // Now sort so that PDF-files are at the top
        f = f.sort(pdfSorter)

        // In case the paths are not absolute, make them this way.
        if (!path.isAbsolute(f[0])) {
          f = f.map(elem => path.join(baseDir, elem))
        }
        // Save them to the dictionary
        files[entry.id] = f
      }
    }

    // If the entry has not been assigned by now, this means there
    // are no files attached. -> Set it to false so one can easily
    // check if (!files[key]), as an array will evaluate to true.
    if (files[entry.id] === undefined) {
      files[entry.id] = false
    }
  }

  return files
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        extractLinefeed
 * CVM-Role:        Utility Function
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     This function extracts the correct linefeed from a file.
 *
 * END HEADER
 */

type Linefeed = '\n'|'\r'|'\r\n'|'\n\r'

export function extractLinefeed (text: string): Linefeed {
  const CR = text.includes('\r')
  const LF = text.includes('\n')
  const CRLF = text.includes('\r\n')
  const LFCR = text.includes('\n\r')

  const indexCRLF = CRLF ? text.indexOf('\r\n') : Infinity
  const indexLFCR = LFCR ? text.indexOf('\n\r') : Infinity

  if (LF && !CR) {
    return '\n' // Unix-style (Linux/macOS)
  } else if (CR && !LF) {
    return '\r' // Commodore 64 and old Apple II systems, also emails afaik
  } else if (CRLF && indexCRLF < indexLFCR) {
    return '\r\n' // Windows and MS-DOS
  } else if (LFCR && indexLFCR < indexCRLF) {
    return '\n\r' // According to Wikipedia, only Acorn BBC and RISC OS
  } else {
    return '\n' // By default, assume a simple newline
  }
}

/**
 * BEGIN HEADER
 *
 * Contains:        Utility function
 * CVM-Role:        <none>
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     Extracts a YAML frontmatter
 *
 * END HEADER
 */

import YAML from 'yaml'

interface ExtractYamlFrontmatterReturn {
  frontmatter: Record<string, any>|null
  content: string
}

/**
 * Takes Markdown source and returns a parsed YAML frontmatter as an object or
 * null (if there was no frontmatter). NOTE: This function will always return
 * either null or an object with keys and values, as frontmatter in the context
 * of this function does NOT simply mean "valid YAML" (since that could also be
 * just a string or a number). So if the frontmatter consists solely of a
 * primitive or a list, this function will remove the frontmatter from the
 * content BUT return an empty object. This makes it easy to work with the
 * returned object but is strictly speaking opiniated.
 *
 * @param   {string}    markdown  The Markdown source
 *
 * @return  {any|null}            The parsed frontmatter as an object, or null.
 */
export default function extractYamlFrontmatter (markdown: string): ExtractYamlFrontmatterReturn {
  // Shortcut, since most files can be expected not to have a frontmatter
  if (!markdown.startsWith('---')) {
    return { frontmatter: null, content: markdown }
  }

  const ret: ExtractYamlFrontmatterReturn = {
    frontmatter: null,
    content: markdown
  }

  let linefeed = '\n'
  if (markdown.includes('\r\n')) linefeed = '\r\n'
  if (markdown.includes('\n\r')) linefeed = '\n\r'

  const lines = markdown.split(linefeed)

  lines.unshift('') // For the start algorithm
  let start = -1
  let end = -1

  for (let i = 1; i < lines.length; i++) {
    // Either first line or in between, in both cases
    // this condition holds true due to the unshift above
    if (lines[i] === '---' && lines[i - 1] === '') {
      start = i
      break
    }
  }

  if (start < 0) {
    return ret // No frontmatter
  }

  for (let i = start + 1; i < lines.length; i++) {
    if ([ '---', '...' ].includes(lines[i])) {
      end = i - 1
      break
    }
  }

  if (end < 0) {
    return ret // The frontmatter did not end
  }

  // Now we have a frontmatter (if there was any) -> extract!
  let frontmatter = ''
  for (let i = start; i <= end; i++) {
    frontmatter += '\n' + lines[i]
  }

  // Parse and return
  try {
    const parsedFrontmatter = YAML.parse(frontmatter)
    const isPrimitive = [ 'string', 'number', 'boolean' ].includes(typeof parsedFrontmatter)

    if (!isPrimitive && !Array.isArray(parsedFrontmatter)) {
      ret.frontmatter = parsedFrontmatter
    } else {
      // We have a frontmatter but it only consists of either a primitive or an
      // array. Since this function is assumed to return a Pandoc-parseable
      // frontmatter, we need to overwrite it here.
      ret.frontmatter = {}
    }

    // Remove frontmatter from content.
    ret.content = lines.slice(end + 2).join(linefeed)
    return ret
  } catch (err) {
    return ret
  }
}

/**
 * BEGIN HEADER
 *
 * Contains:        Utility function
 * CVM-Role:        <none>
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     The contained file can find an object in an arbitrary tree.
 *
 * END HEADER
 */

/**
 * Traverses an arbitrary recursive object tree to find an object with a specific prop=val.
 *
 * @param   {any}            tree     The tree to traverse
 * @param   {string}         prop     The property that should be matched
 * @param   {any}            val      The value that the property should be checked for
 * @param   {string}         traverse The property over which the function traverses the tree
 *
 * @returns {any|undefined}           Either the correct object, or undefined
 */
export default function findObject (tree: any, prop: string, val: any, traverse: string): any|undefined {
  // Is the tree even defined?
  if (tree == null) {
    return undefined
  }

  // First let's see if we can shortcut
  if (!Array.isArray(tree) && prop in tree && tree[prop] === val) {
    return tree
  }

  // Now search the tree
  if (Array.isArray(tree)) {
    // The tree itself is an array
    for (const item of tree) {
      const ret = findObject(item, prop, val, traverse)
      if (ret !== undefined) {
        return ret
      }
    }
  } else if (traverse in tree && Array.isArray(tree[traverse])) {
    // The descendants are an array
    for (const descendant of tree[traverse]) {
      const ret = findObject(descendant, prop, val, traverse)
      if (ret !== undefined) {
        return ret
      }
    }
  } else if (traverse in tree) {
    // Neither tree nor descendants are an array -> simple traverse
    return findObject(tree[traverse], prop, val, traverse)
  }
}

