import { DateTime } from 'luxon'
import { trans } from '../i18n-renderer'

/**
 * Formats a date based on the given locale.
 *
 * @param   {Date|number}  dateObj           A JavaScript Date object or a timestamp in milliseconds
 * @param   {string}       locale            The locale to use for the formatter
 * @param   {boolean}      [relative=false]  Optional. If true, output a relative timestamp
 *
 * @return  {string}                         The formatted date string
 */
export default function formatDate (dateObj: Date|number, locale: string, relative: boolean = false): string {
  const isDate = dateObj instanceof Date
  const dt = (isDate) ? DateTime.fromJSDate(dateObj) : DateTime.fromMillis(dateObj)
  if (relative) {
    // Check if there is at least a minute difference between the datetime object
    // and now. If not, simply output "just now", else the actual relative difference.
    if (dt.diff(DateTime.now(), 'minutes').toObject().minutes as number * -1 < 1) {
      return trans('just now')
    } else {
      return dt.toRelative({
        style: 'short', // Can be short, narrow, or long
        locale
      }) ?? ''
    }
  } else {
    return dt.toLocaleString(
      { dateStyle: 'long', timeStyle: 'short' },
      { locale }
    )
  }
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        Trans-function for the renderer
 * CVM-Role:        Utility Function
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     This is the trans-function that is specifically made for
 *                  sandboxed renderer processes.
 *
 * END HEADER
 */

import sanitizeHtml from 'sanitize-html'
const ipcRenderer = window.ipc

let i18nData: any

/**
 * Call this function during window registration to load the translation data
 * immediately after start so that the translations are available. NOTE: This
 * function produces a side-effect in that it sets the local module variable.
 */
export async function loadData (): Promise<void> {
  i18nData = await ipcRenderer.invoke('i18n')
}

/**
 * Takes a message ID and returns the appropriate translated message string. If
 * there is no language data loaded, this function returns the message ID.
 *
 * @param   {string}  msgid  The message ID to return a translation for
 *
 * @return  {string}         The translation, or the message ID if no translations were found.
 */
function getTranslation (msgid: string): string {
  if (i18nData === undefined) {
    return msgid
  }

  const context = i18nData.translations['']

  if (msgid in context && context[msgid].msgstr[0] !== '') {
    return context[msgid].msgstr[0]
  } else {
    return msgid
  }
}

/**
 * Translates the given message ID
 *
 * @param   {string}  msgid  The message ID to translate
 * @param   {any[]}   args   Provide optional arguments to replace in the
 *                           translation. One argument replaces one %s, in order.
 *
 * @return  {string}         The translated and replaced string.
 */
export function trans (msgid: string, ...args: any[]): string {
  let transString = getTranslation(msgid)

  for (const a of args) {
    transString = transString.replace('%s', a) // Always replace one %s with an arg
  }

  // Finally, before returning the translation, sanitize it. As these are only
  // translation strings, we can basically only allow a VERY small subset of all
  // tags.
  const safeString = sanitizeHtml(transString, {
    allowedTags: [ 'em', 'strong', 'kbd' ]
  })

  return safeString
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        DirectedGraph
 * CVM-Role:        Model
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     This class implements a directed graph
 *
 * END HEADER
 */

import type { GraphArc, GraphVertex, LinkGraph } from '@dts/common/graph'

const NONE_COMPONENT = 'Files'

export default class DirectedGraph {
  private readonly _arcs: GraphArc[]
  private readonly _vertices: GraphVertex[]
  private _components: string[]
  private _inOperation: boolean

  constructor () {
    this._arcs = []
    this._vertices = []
    this._components = []
    this._inOperation = false
  }

  get graph (): LinkGraph {
    if (this._inOperation) {
      throw new Error('Graph is currently in an operation. Did you forget to call endOperation?')
    }
    return {
      nodes: this._vertices,
      links: this._arcs,
      components: this._components
    }
  }

  startOperation (): void {
    this._inOperation = true
  }

  endOperation (): void {
    this._inOperation = false
    // Now we can recalculate all the metrics and run the algorithms
    this.identifyComponents()
  }

  get countVertices (): number {
    return this._vertices.length
  }

  get countArcs (): number {
    return this._arcs.length
  }

  get countIsolates (): number {
    return this._vertices.filter(V => V.isolate).length
  }

  get countComponents (): number {
    return this._components.length
  }

  get vertices (): GraphVertex[] {
    return this._vertices
  }

  addVertex (id: string, label?: string): GraphVertex {
    const foundV = this._vertices.find(V => V.id === id)
    if (foundV !== undefined) {
      return foundV
    }

    const newV = {
      id,
      label,
      component: NONE_COMPONENT,
      isolate: true
    }

    this._vertices.push(newV)
    if (!this._inOperation) {
      this.identifyComponents()
    }
    return newV
  }

  addArc (source: string, target: string, weight: number = 1): void {
    let sV = this._vertices.find(V => V.id === source)
    let tV = this._vertices.find(V => V.id === target)

    if (sV === undefined) {
      sV = this.addVertex(source)
    }
    if (tV === undefined) {
      tV = this.addVertex(target)
    }

    sV.isolate = false
    tV.isolate = false
    this._arcs.push({ source, target, weight })

    if (!this._inOperation) {
      this.identifyComponents()
    }
  }

  setLabel (vertex: string, label: string): void {
    const V = this._vertices.find(ver => ver.id === vertex)

    if (V === undefined) {
      throw new Error(`Could not set label for V(${vertex})!`)
    }

    V.label = label
  }

  private identifyComponents (): void {
    // First, reset all components
    for (const V of this._vertices) {
      V.component = NONE_COMPONENT
    }

    // We need to also reset the components
    this._components = []

    const visit = (source: GraphVertex, component?: string): void => {
      // This node already is part of a component
      if (source.component !== NONE_COMPONENT) {
        return
      }

      // Now we know that this vertex does not yet have a component, i.e. it's
      // not part of an already existing component. So create a new one
      if (component === undefined) {
        component = `Component ${this._components.length + 1}`
        this._components.push(component)
      }

      source.component = component

      // NOTE: Here we must treat the network as undirected in order to create
      // components. For this, we retrieve every arc that has the current vertex
      // either as its head or tail, then map to the opposite end of the arc and
      // resolve to the nodes.
      const allTargets = this._arcs
        .filter(link => link.source === source.id || link.target === source.id)
        .map(link => {
          if (link.target === source.id) {
            return link.source
          } else {
            return link.target
          }
        })

      for (const target of allTargets) {
        const V = this._vertices.find(V => V.id === target)
        if (V === undefined) {
          throw new Error(`${target} for ${source.id} was not defined!`)
        }
        visit(V, component)
      }
    }

    for (const V of this._vertices) {
      visit(V)
    }
  }
}

