/**
 * Returns a regular expression that matches file IDs as in the settings
 *
 * @param   {boolean}  exact      If true, makes sure that the full string needs to match
 *
 * @return  {RegExp}              The compiled Regular Expression
 */
export function getIDRE (idGenPattern: string, exact: boolean = false): RegExp {
  let idRegExpString: string = idGenPattern
  // Make sure the ID definitely has at least one
  // capturing group to not produce errors.
  if (!(/\(.+?\)/.test(idRegExpString))) {
    idRegExpString = `(${idRegExpString})`
  }

  return RegExp(
    (exact) ? `^${idRegExpString}$` : idRegExpString,
    'g'
  )
}

/**
 * Returns a regular expression that matches Markdown links.
 *
 * @param   {boolean}  global  whether the expression should match globally
 *
 * @return  {RegExp}           The compiled Regular Expression
 */
export function getLinkRE (global: boolean = false): RegExp {
  return RegExp(
    /^.+\.[a-z0-9]+/.source,
    (global) ? 'gi' : 'i')
}

/**
 * Returns a regular expression that matches URL protocols (e.g. http://)
 *
 * @param   {boolean}  multiline  Whether or not the expression should be multiline
 *
 * @return  {RegExp}           The wanted regular expression
 */
export function getProtocolRE (multiline: boolean = false): RegExp {
  let flag = (multiline) ? 'm' : ''
  return RegExp(
    /^([a-z]{1,10}):\/\//,
    'i' + flag
  )
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        Rendererer path polyfill functions
 * CVM-Role:        <none>
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     This module contains a subset of Node.js's path functions
 *                  which we need despite the renderer sandbox. NOTE: This is a
 *                  subset that only implements required functions and not deals
 *                  with all edge cases, only the relevant ones!
 *
 * END HEADER
 */

/**
 * Returns the correct path separator for the given path.
 *
 * @param   {string}  path  The path to check
 *
 * @return  {string}        Either \ or /
 */
function sep (path: string): '\\'|'/' {
  return isWin32Path(path) ? '\\' : '/'
}

/**
 * Utility function that checks if a path is likely a win32-path
 *
 * @param   {string}   path  The path to check
 *
 * @return  {boolean}        Returns true if the path includes backslashes but
 *                           no forward slashes.
 */
export function isWin32Path (path: string): boolean {
  // We make use of the fact that on Windows, forward slashes are forbidden in
  // path names, and it is unusual to see backslashes in Unix paths.
  return path.includes('\\') && !path.includes('/')
}

/**
 * Returns true if provided path equals the root on a Windows or Unix machine.
 *
 * @param   {string}   path  The path to check
 *
 * @return  {boolean}        True if it equals the root directory
 */
function isRootDir (path: string): boolean {
  return path === '/' || /^[A-Z]:\\$/i.test(path)
}

/**
 * Returns true if the given path is absolute.
 *
 * @param   {string}   path  The path to check
 *
 * @return  {boolean}        True if it's absolute
 */
export function isAbsolutePath (path: string): boolean {
  if (path.length < 2) {
    return false // Invalid length
  } else if (path.length >= 1 && path.startsWith('/')) {
    return true // Unix
  } else if (path.length >= 3 && (/^[A-Z]:[\\/]/i.test(path) || path.startsWith('\\\\'))) {
    return true // Windows (drive letters + network drives)
  } else {
    return false
  }
}

/**
 * Returns the relative path from from to to.
 *
 * @param   {string}  from  The source location
 * @param   {string}  to    The target location
 *
 * @return  {string}        The relative path
 */
export function relativePath (from: string, to: string): string {
  if (sep(from) !== sep(to)) {
    throw new Error('Cannot calculate relative path between win32 and Unix paths')
  }

  // Edge cases
  if (to.startsWith(from)) {
    // to is already the relative path
    return to.substring(from.length + 1) // Remove path separator
  } else if (isWin32Path(from) && isWin32Path(to) && to[0].toLowerCase() !== from[0].toLowerCase()) {
    // Files are on different drives
    return to
  } /* else if (pathDirname(from) === pathDirname(to)) {
    // Same directory
    return pathBasename(to)
  } */

  // After the edge cases, we have to do some more work. Finding the relative
  // path is a combination of adding '..' and parts of either path to the other.
  const fromSegments = from.split(sep(from))
  const toSegments = to.split(sep(to))
  if (fromSegments.length === 1) {
    // Happens with, e.g., filenames. Node.js's path assumes traversing up from
    // both path starts here
    fromSegments.unshift('..')
    toSegments.unshift('..')
  }
  let lastCommonSegment = 0
  for (let i = 0; i < Math.min(fromSegments.length, toSegments.length); i++) {
    if (fromSegments[i] === toSegments[i]) {
      lastCommonSegment = i
    } else {
      break
    }
  }

  // Now we have to do two things: (1) move up from from to the last common
  // segment and then (2) append the remaining segments from to
  const seq = `..${sep(from)}`.repeat(fromSegments.length - lastCommonSegment - 1)
  const appendix = toSegments.slice(lastCommonSegment + 1).join(sep(to))
  const relativePath = seq + appendix
  return relativePath.endsWith(sep(from)) ? relativePath.substring(0, relativePath.length - 1) : relativePath
}

/**
 * Resolves the provided (relative) path using the (absolute) base to an
 * absolute path to the resource.
 *
 * @param   {string}  base  The base.
 * @param   {string}  path  The path.
 *
 * @return  {string}        The path, turned absolute.
 */
export function resolvePath (base: string, path: string): string {
  if (isAbsolutePath(path)) {
    return path
  }

  const pathSegments = path.split(sep(path))
  if (pathSegments[0] === '.') {
    // Remove a potential this-dir indicator
    pathSegments.splice(0, 1)
  }

  // NOTE: We reverse the array, which makes splicing path segments easier.
  const baseSegments = base.split(sep(base)).reverse()

  for (let i = 0; i < pathSegments.length; i++) {
    const segment = pathSegments[i]
    // For each "go up", remove one base segment
    if (segment === '..') {
      baseSegments.splice(0, 1) // NOTE: basePath is reversed
      pathSegments.splice(0, 1)
      i--
    } else {
      break
    }
  }

  // This ensures that the resolved path ends with a separator
  if (pathSegments.length === 0) {
    pathSegments.push('')
  }

  return [ ...baseSegments.reverse(), ...pathSegments ].join(sep(base))
}

/**
 * Returns the last part of a path, i.e. either a filename or a directory name.
 *
 * @param   {string}  path     The path to extract from
 * @param   {string}  extname  An optional extension to strip from the basename
 *
 * @return  {string}           The basename
 */
export function pathBasename (path: string, extname?: string): string {
  const basename = path.substring(path.lastIndexOf(sep(path)) + 1)
  if (extname !== undefined && basename.endsWith(extname)) {
    return basename.substring(0, basename.length - extname.length)
  } else {
    return basename
  }
}

/**
 * Returns the path extension (which can also be .app for folder bundles on
 * macOS).
 *
 * @param   {string}  path  The path to extract from
 *
 * @return  {string}        The extension name; empty string if no extension
 */
export function pathExtname (path: string): string {
  return path.includes('.') ? path.substring(path.lastIndexOf('.')) : ''
}

/**
 * Returns the directory name of a path (i.e., the parent directory). Returns
 * identity if path is the root directory, and an empty string if it describes
 * only a folder/file. NOTE: This function returns '..' or '.' if applicable.
 *
 * @param   {string}  path  The path to extract from
 *
 * @return  {string}        The directory name
 */
export function pathDirname (path: string): string {
  if (isRootDir(path)) {
    return path
  }
  const s = sep(path)
  return path.includes(s) ? path.substring(0, path.lastIndexOf(s)) : '.'
}

import locateByPath from '@providers/fsal/util/locate-by-path'
import type { AnyDescriptor, DirDescriptor } from '@dts/common/fsal'
import type { ChangeDescriptor } from './root'
import { type GenericSorter } from '@providers/fsal/util/directory-sorter'

const PATH_SEP = process.platform === 'win32' ? '\\' : '/'

/**
 * This function takes a series of change events for the file trees and merges
 * those one after another into the provided tree, returning the new tree. NOTE:
 * This requires that the events are actually accumulated for this tree;
 * providing another tree will lead to errors and inconsistencies. NOTE: This
 * function will also be imported by the renderer so DO NOT USE ANY MAIN PROCESS
 * DEPENDENCIES!
 *
 * @param   {ChangeDescriptor[]}  events  The list of changes
 * @param   {AnyDescriptor}       tree    The tree to merge the changes into
 *
 * @return  {AnyDescriptor}               The modified tree
 */
export function mergeEventsIntoTree (events: ChangeDescriptor[], tree: AnyDescriptor, sortFunction: GenericSorter): AnyDescriptor {
  let descriptorToReturn = tree

  for (const event of events) {
    if (event.type === 'add') {
      if (locateByPath(descriptorToReturn, event.descriptor.path) !== undefined) {
        console.error(`Received an add event for a path that is already present in the tree -- not merging in (${event.descriptor.path}).`)
        continue
      }

      // Find the parent, and add the given descriptor to its children
      const parent = locateByPath(descriptorToReturn, event.descriptor.dir)
      if (parent === undefined || parent.type !== 'directory') {
        throw new Error(`[mergeEventsIntoTree] add:${event.descriptor.path}: Local tree did not contain the descriptor's parent.`)
      }

      parent.children.push(event.descriptor)
      parent.children = sortFunction(parent.children, parent.settings.sorting)
    } else if (event.type === 'change' && event.path === descriptorToReturn.path) {
      // The descriptor itself has changed (NOTE: We are checking both descriptors for type reasons)
      if (event.descriptor.type === 'directory' && descriptorToReturn.type === 'directory') {
        const existingChildren = descriptorToReturn.children
        descriptorToReturn = event.descriptor
        descriptorToReturn.children = existingChildren
        // A "change" is also when the sorting has changed.
        descriptorToReturn.children = sortFunction(descriptorToReturn.children, descriptorToReturn.settings.sorting)
      } else {
        descriptorToReturn = event.descriptor
      }
    } else if (event.type === 'change') {
      const parent = locateByPath(descriptorToReturn, event.descriptor.dir)

      if (parent === undefined || parent.type !== 'directory') {
        throw new Error(`[mergeEventsIntoTree] change:${event.path}: Local tree did not contain the descriptor's parent.`)
      }

      const idx = parent.children.findIndex(desc => desc.path === event.path)

      if (idx < 0) {
        throw new Error(`[mergeEventsIntoTree] change:${event.path}: Could not find the old descriptor in the parent.`)
      }

      if (event.descriptor.type === 'directory') {
        // Ensure to carry over the recursive children array
        event.descriptor.children = (parent.children[idx] as DirDescriptor).children
      }

      parent.children.splice(idx, 1, event.descriptor)
    } else {
      // Unlink event
      // NOTE: We cannot use path here since this function is also required in
      // renderer processes
      const dirname = event.path.substring(0, event.path.lastIndexOf(PATH_SEP))
      const parent = locateByPath(descriptorToReturn, dirname)
      if (parent === undefined || parent.type !== 'directory') {
        throw new Error(`[mergeEventsIntoTree] unlink:${event.path}: Could not find the descriptor's parent.`)
      }

      const idx = parent.children.findIndex(desc => desc.path === event.path)

      if (idx < 0) {
        throw new Error(`[mergeEventsIntoTree] unlink:${event.path}: Could not remove descriptor from tree.`)
      }

      parent.children.splice(idx, 1)
    }
  }

  return descriptorToReturn
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        locateByPath
 * CVM-Role:        Utility Function
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     A small utility function that takes a directory tree and
 *                  returns the descriptor for the given path, if available.
 *
 * END HEADER
 */
import type { AnyDescriptor } from '@dts/common/fsal'

const PATH_SEP = process.platform === 'win32' ? '\\' : '/'

/**
 * Takes a tree of descriptors and returns the descriptor that exactly matches
 * targetPath, or undefined.
 *
 * @param   {AnyDescriptor}            tree        One or more descriptors
 * @param   {string}                   targetPath  The (absolute) path to search for
 *
 * @return  {AnyDescriptor|undefined}              Either the descriptor or undefined
 */
export default function locateByPath (tree: AnyDescriptor|AnyDescriptor[], targetPath: string): AnyDescriptor|undefined {
  // We need to find a target
  if (Array.isArray(tree)) {
    for (const descriptor of tree) {
      if (targetPath === descriptor.path) {
        // We have the correct element
        return descriptor
      } else if (targetPath.startsWith(descriptor.path + PATH_SEP) && descriptor.type === 'directory') {
        // We have the correct tree
        return locateByPath(descriptor.children, targetPath)
      }
    }
  } else if (tree.type === 'directory') {
    // Single tree element
    if (targetPath === tree.path) {
      // Found the element
      return tree
    }

    for (const child of tree.children) {
      if (targetPath === child.path) {
        // We got the correct child
        return child
      } else if (targetPath.startsWith(child.path + PATH_SEP) && child.type === 'directory') {
        // Traverse further down
        return locateByPath(child.children, targetPath)
      }
    }
  }

  return undefined
}

/**
 * BEGIN HEADER
 *
 * Contains:        Utility function
 * CVM-Role:        <none>
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     This file contains a utility function to sort directories.
 *
 * END HEADER
 */

export interface RequiredSortingProps {
  type: string
  name: string
  frontmatter?: any
  firstHeading?: string|null
  modtime: number
  creationtime: number
}

type SortingType = 'name-up'|'name-down'|'time-up'|'time-down'
type FilenameDisplay = 'filename'|'title'|'heading'|'title+heading'

export type GenericSorter = <T extends RequiredSortingProps>(arr: T[], type?: SortingType) => T[]

/**
 * Helper function to sort files by modification or creation time
 *
 * @param   {'modtime'|'creationtime'}  whichTime  How to sort
 *
 * @return  {Function}                             Function compatible with
 *                                                 array.sort()
 */
function getDateSorter (whichTime: 'modtime'|'creationtime'): (a: any, b: any) => number {
  return function sortDate<T extends RequiredSortingProps> (a: T, b: T): number {
    let aDate = a.modtime
    let bDate = b.modtime

    if (whichTime !== 'modtime') {
      aDate = a.creationtime
      bDate = b.creationtime
    }

    if (aDate > bDate) {
      return -1
    } else if (aDate < bDate) {
      return 1
    } else {
      return 0
    }
  }
}

/**
 * Helper function to sort files using a collator
 *
 * @param   {FilenameDisplay}  fileNameDisplay  How to sort the files
 * @param   {string}           appLang          The application language
 *
 * @return  {Function}                          Function compatible with
 *                                              array.sort()
 */
function getNaturalSorter (fileNameDisplay: FilenameDisplay, appLang: string): (a: any, b: any) => number {
  return function sortNatural <T extends RequiredSortingProps> (a: T, b: T): number {
    let aSort = a.name.toLowerCase()
    let bSort = b.name.toLowerCase()

    const aTitle = (a.type === 'file') ? typeof a.frontmatter?.title === 'string' : false
    const bTitle = (b.type === 'file') ? typeof b.frontmatter?.title === 'string' : false
    const aHeading = (a.type === 'file') ? a.firstHeading != null : false
    const bHeading = (b.type === 'file') ? b.firstHeading != null : false

    const useH1 = fileNameDisplay.includes('heading')
    const useTitle = fileNameDisplay.includes('title')

    // Use a heading level 1 if applicable, and, optionally, overwrite this with
    // the YAML frontmatter title variable

    if (aHeading && useH1) {
      aSort = a.firstHeading as string
    }

    if (bHeading && useH1) {
      bSort = b.firstHeading as string
    }

    if (aTitle && useTitle) {
      aSort = a.frontmatter.title
    }

    if (bTitle && useTitle) {
      bSort = b.frontmatter.title
    }

    const coll = new Intl.Collator([ appLang, 'en' ], { numeric: true })

    return coll.compare(aSort, bSort)
  }
}

/**
 * Helper function to sort files by ascii characters
 *
 * @param   {FilenameDisplay}  fileNameDisplay  How to sort the files
 *
 * @return  {number}                            Function compatible with
 *                                              array.sort()
 */
function getAsciiSorter (fileNameDisplay: FilenameDisplay): (a: any, b: any) => number {
  return function sortAscii <T extends RequiredSortingProps> (a: T, b: T): number {
    let aSort = a.name.toLowerCase()
    let bSort = b.name.toLowerCase()

    const aTitle = (a.type === 'file') ? typeof a.frontmatter?.title === 'string' : false
    const bTitle = (b.type === 'file') ? typeof b.frontmatter?.title === 'string' : false
    const aHeading = (a.type === 'file') ? a.firstHeading != null : false
    const bHeading = (b.type === 'file') ? b.firstHeading != null : false

    const useH1 = fileNameDisplay.includes('heading')
    const useTitle = fileNameDisplay.includes('title')

    // Use a heading level 1 if applicable, and, optionally, overwrite this with
    // the YAML frontmatter title variable

    if (aHeading && useH1) {
      aSort = a.firstHeading as string
    }

    if (bHeading && useH1) {
      bSort = b.firstHeading as string
    }

    if (aTitle && useTitle) {
      aSort = a.frontmatter.title
    }

    if (bTitle && useTitle) {
      bSort = b.frontmatter.title
    }

    // Negative return: a is smaller b (case insensitive)
    if (aSort < bSort) {
      return -1
    } else if (aSort > bSort) {
      return 1
    } else {
      return 0
    }
  }
}

/**
 * Returns a function that can sort a children-array on a directory descriptor.
 *
 * @param   {'natural'|'ascii'}         sortingType       How to sort characters
 * @param   {boolean}                   sortFoldersFirst  Whether to sort
 *                                                        folders first
 * @param   {FilenameDisplay}           fileNameDisplay   Which property to sort
 *                                                        on when sorting by
 *                                                        name
 * @param   {string}                    appLang           The application
 *                                                        language
 * @param   {'modtime'|'creationtime'}  whichTime         How to sort when
 *                                                        sorting by time
 *
 * @return  {GenericSorter}                               Returns a sorter
 *                                                        function
 */
export function getSorter (
  sortingType: 'natural'|'ascii',
  sortFoldersFirst: boolean,
  fileNameDisplay: FilenameDisplay,
  appLang: string,
  whichTime: 'modtime'|'creationtime'
): GenericSorter {
  return function sort <T extends RequiredSortingProps> (arr: T[], type: SortingType = 'name-up'): T[] {
    // First split the array based on type
    const f: T[] = []
    const d: T[] = []

    // Should we use natural sorting or ascii?
    const useNatural = sortingType === 'natural'

    // Write in the sortingFunc whatever we should be using
    const sortingFunc = (useNatural) ? getNaturalSorter(fileNameDisplay, appLang) : getAsciiSorter(fileNameDisplay)

    // Split up the children list
    for (const c of arr) {
      switch (c.type) {
        case 'file':
        case 'code':
        case 'other':
          f.push(c)
          break
        case 'directory':
          d.push(c)
          break
      }
    }

    // Sort the directories (always based on name)
    d.sort(sortingFunc)

    // Now sort the files according to the type of sorting
    switch (type) {
      case 'name-up':
        f.sort(sortingFunc)
        break
      case 'name-down':
        f.sort(sortingFunc).reverse()
        break
      case 'time-up':
        f.sort(getDateSorter(whichTime))
        break
      case 'time-down':
        f.sort(getDateSorter(whichTime)).reverse()
        break
    }

    // Return sorted array files -> directories
    if (sortFoldersFirst) {
      return d.concat(f)
    } else {
      return f.concat(d)
    }
  }
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        moveSection function
 * CVM-Role:        Utility function
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     Swaps sections demarcated by headers within Markdown files.
 *
 * END HEADER
 */

/**
 * Moves a whole section (as demarcated by ATX headings)
 * @param  {number} fromLine The line at which the section to be moved begins
 * @param  {number} toLine   The target line, above which the section should be inserted.
 * @return {string}    The new file contents with the section moved.
 */
export default function moveSection (value: string, fromLine: number, toLine: number): string {
  let lines = value.split('\n')
  let sectionStart = fromLine
  let sectionEnd = fromLine
  let headingLevel = -1

  // First match of the following regex contains the heading characters, ergo
  // the length is the heading level
  headingLevel = (/^(#{1,6}) (.*)$/.exec(lines[fromLine]) ?? []).length

  if (headingLevel === 0) {
    return value // The caller has passed the wrong fromLine
  }

  // Build a regex to be used now. We'll only stop at either a higher or
  // same level heading. We're doing this, because this way we'll include
  // lesser headings in this section.
  const searchRegex = new RegExp(`^#{1,${headingLevel}} .+$`)
  for (let i = sectionStart + 1; i < lines.length; i++) {
    if (searchRegex.test(lines[i])) {
      // We've found a heading of at least this level.
      sectionEnd = i - 1 // Don't include the current line, obviously
      break
    }
  }

  // Sanity check: If sectionEnd has not been set, this means that the user
  // wanted to move the final section -- the RegExp will naturally not yield
  // any result, so we take everything until the very end to be included in
  // the section.
  if (sectionEnd === fromLine) {
    sectionEnd = lines.length - 1
  }

  let section = lines.slice(sectionStart, sectionEnd + 1)

  if (toLine < 0) {
    // We should move the section to the end, so cut and append it.
    // Remove the old section.
    lines.splice(sectionStart, section.length)
    // Sneak a new line into the section, if there's no padding
    if (lines[lines.length - 1] !== '') section.unshift('')
    // Concat the section
    lines = lines.concat(section)

    // Remove a trailing newline if applicable
    if (lines[lines.length - 1] === '') lines.pop()
  } else if (sectionEnd < toLine) {
    // The section should be moved to the back, so to not confuse line numbers,
    // we first need to insert the section (i.e. copy and paste), and only
    // afterwards remove the section.
    // First get the stuff before the old section position
    let beforeSection = lines.slice(0, sectionStart)
    // Then append the part behind the section up until the target line
    beforeSection = beforeSection.concat(lines.slice(sectionEnd + 1, toLine))

    // Get everything after the target line
    let afterSection = lines.slice(toLine)

    // Now glue it back together (afterSection -> section, then section -> beforeSection)
    lines = beforeSection.concat(section.concat(afterSection))
  } else if (sectionStart > toLine) {
    // The section should be moved to the front, so we can safely cut it directly.
    // Remove the old section.

    // It was in fact the last section, so let's add a newline afterwards.
    if (sectionEnd + 1 === lines.length) {
      // ... but only if it doesn't end with a newline
      if (section[section.length - 1] !== '') section.push('')
    }

    // Now splice out the section
    lines.splice(sectionStart, section.length)

    // Now that the file has been truncated by the section, let's make sure
    // it does not end with a newline
    if (lines[lines.length - 1] === '') {
      lines.pop()
    }

    // Then insert it above the target line.
    lines.splice(toLine, 0, ...section)
  }

  // Now we have the correct lines. So let's simply replace the whole content
  // with it. Tadaa!
  return lines.join('\n')
}

/**
* @ignore
* BEGIN HEADER
*
* Contains:        TableHelper utility function
* CVM-Role:        Utility
* Maintainer:      Hendrik Erz
* License:         GNU GPL v3
*
* Description:     This parser transforms grid tables as specified
*                  in the Pandoc manual into an AST and returns both
*                  that and the column alignments.
*                  Cf. https://pandoc.org/MANUAL.html#tables
*
* END HEADER
*/

import type { ParsedTable, ColAlignment } from './types'

/**
 * Parses a grid table
 * @param {String|Array} A markdown table, either as line array or string
 * @returns {Object} An object with properties ast and colAlignments
 */
export default function parseGridTable (markdownTable: string|string[]): ParsedTable {
  // First remove whitespace from both sides of the table, e.g. in case
  // a trailing newline is present
  if (typeof markdownTable === 'string') {
    markdownTable = markdownTable.trim()
  }

  if (!Array.isArray(markdownTable)) {
    markdownTable = markdownTable.split('\n')
  }

  if (markdownTable.length === 0 || (markdownTable.length === 1 && markdownTable[0].trim() === '')) {
    throw new SyntaxError('Could not parse grid table: empty!')
  }

  const ast: string[][] = []
  const colAlignments: ColAlignment[] = [] // One-dimensional column alignments
  let numColumns = 0 // If there is an uneven number of columns, throw an error.

  // The speciality of grid tables is that they can contain the alignment info
  // either in the first row, or in the second separator row. So we have to find
  // the alignment prior to iterating over the rows.
  let headerRow = 0
  for (let i = 0; i < markdownTable.length; i++) {
    if (/^[:+= ]+$/.test(markdownTable[i])) { // "Real" headers have = instead of -
      headerRow = i
      break
    }
  }

  let headerContent: string|string[] = markdownTable[headerRow]

  headerContent = headerContent.split('+').slice(1, -1)
  for (let i = 0; i < headerContent.length; i++) {
    const col = headerContent[i].trim()
    if (col.startsWith(':') && col.endsWith(':')) {
      colAlignments[i] = 'center'
    } else if (col.endsWith(':')) {
      colAlignments[i] = 'right'
    } else {
      colAlignments[i] = 'left'
    }
  }

  // Now iterate over all table rows
  let hasSeenSeparatorLine = false
  for (let i = 0; i < markdownTable.length; i++) {
    const row = markdownTable[i]

    // There should not be empty lines in the table.
    // If so, this indicates an error in the render tables plugin!
    if (row.trim() === '') {
      throw new SyntaxError(`Could not parse grid table: Line ${i + 1} was empty!`)
    }

    if (row.startsWith('+') && hasSeenSeparatorLine) {
      throw new SyntaxError('Could not parse grid table: Found multiple separator rows!')
    }

    if (row.startsWith('+')) {
      hasSeenSeparatorLine = true
      continue // Skip separation lines
    }

    if (!row.startsWith('|')) {
      throw new Error(`Could not parse grid table: Row ${i + 1} did not start with + or |!`)
    }

    // Split to columns
    const cols = row.split('|').map(elem => elem.trim()).slice(1, -1)

    if (numColumns === 0) {
      numColumns = cols.length // First row determines column count ...
    } else if (numColumns !== cols.length) { // ... subsequent rows check against this.
      throw new SyntaxError(`Could not parse grid table: Found ${row.length} columns on line ${i + 1} (should be ${numColumns}).`)
    }

    if (hasSeenSeparatorLine) {
      // Add the whole row to the AST
      ast.push(cols)
    } else {
      // There was no separator line in between --> multi-line table --> add the
      // current columns to the previous ones (including newlines).
      for (let j = 0; j < numColumns; j++) {
        ast[ast.length - 1][j] += '\n' + cols[j]
      }
    }

    hasSeenSeparatorLine = false
  }

  if (ast.length === 0 || ast[0].length === 0) {
    // This AST does not look as it's supposed to -> abort
    throw new SyntaxError('Could not parse grid table: The parsed AST was empty.')
  }

  // Cleanup: Remove trailing newlines (that can occur if we have a row where
  // more than zero but not all columns were multi-line)
  for (let row = 0; row < ast.length; row++) {
    for (let col = 0; col < ast[0].length; col++) {
      if (ast[row][col].endsWith('\n')) {
        ast[row][col] = ast[row][col].slice(0, -1)
      }
    }
  }

  // Return the AST
  return { ast, colAlignments }
}

