import type { ParsedTable, ColAlignment } from './types'

/**
 * Parses a pipe table
 * @param {String|Array} A markdown table, either as line array or string
 * @returns {Object} An object with properties ast and colAlignments
 */
export default function parsePipeTable (markdownTable: string|string[]): ParsedTable {
  // First remove whitespace from both sides of the table, e.g. in case
  // a trailing newline is present
  if (typeof markdownTable === 'string') markdownTable = markdownTable.trim()
  if (!Array.isArray(markdownTable)) markdownTable = markdownTable.split('\n')

  if (markdownTable.length === 0) throw new Error('MarkdownTable was empty!')
  if (markdownTable.length === 1 && markdownTable[0].trim() === '') throw new Error('MarkdownTable was empty!')

  let ast = [] // Two-dimensional array
  let colAlignments: ColAlignment[] = [] // One-dimensional column alignments
  let numColumns = -1 // If there is an uneven number of columns, throw an error.

  // Now iterate over all table rows
  for (let i = 0; i < markdownTable.length; i++) {
    // There should not be empty lines in the table.
    // If so, this indicates an error in the render tables plugin!
    if (markdownTable[i].trim() === '') throw new Error(`Line ${i} in the table was empty!`)
    let row: string[]|string = markdownTable[i].trim() // Clean up whitespace
    if (/^[- :+|]+$/.test(row)) {
      // We have an alternative pipetable, separated with + instead of |,
      // so we have to replace all instances of + with |
      row = row.replace(/\+/g, '|')
    }

    // Split to columns
    row = row.split('|')

    // Now, expect that the first and last "column" are empty and remove them.
    // If they are not empty, we probably have a pipe table without surrounding
    // pipes.
    if (row[0].trim() === '') row.shift()
    if (row[row.length - 1].trim() === '') row.pop()

    // First row determines the amount of columns expected
    if (numColumns === -1) {
      numColumns = row.length // First row determines column count ...
    }

    if (numColumns !== row.length) { // ... subsequent rows check that length.
      throw new Error(`Malformed Markdown Table! Found ${row.length} columns on line ${i} (should be ${numColumns}).`)
    }

    // First test if we've got a header row. A header row is defined of
    // consisting of columns only containing dashes, colons and spaces. The
    // first column to break this rule means we don't have a valid header.
    let isHeader = true
    for (let col of row) {
      if (!/^[-: ]+$/.test(col) || col.trim() === '') {
        // Note we have to check for completely blank lines
        isHeader = false
        break
      }
    }

    // Parse the header
    if (isHeader) {
      for (let j = 0; j < row.length; j++) {
        let col = row[j].trim()
        if (col[0] === ':' && col[col.length - 1] === ':') {
          colAlignments[j] = 'center'
        } else if (col[col.length - 1] === ':') {
          colAlignments[j] = 'right'
        } else {
          colAlignments[j] = 'left'
        }
      }
      continue // We're done here -- don't add it to the AST
    }

    // We have a normal table row, so parse all columns
    let cols = []
    for (let j = 0; j < row.length; j++) {
      cols.push(row[j].trim()) // Trim whitespaces
    }

    // Add the whole row to the AST
    ast.push(cols)
  }

  // If we have reached this stage, but no header row was found,
  // preset all column alignments with left
  if (colAlignments.length === 0) {
    for (let i = 0; i < numColumns; i++) {
      colAlignments.push('left')
    }
  }

  if (ast.length === 0 || ast[0].length === 0) {
    // This AST does not look as it's supposed to -> abort
    throw new Error('Malformed Markdown Table! The parsed AST was empty.')
  }

  // Return the AST
  return { ast, colAlignments }
}

/**
* @ignore
* BEGIN HEADER
*
* Contains:        TableHelper utility function
* CVM-Role:        Utility
* Maintainer:      Hendrik Erz
* License:         GNU GPL v3
*
* Description:     This parser transforms simple tables as specified
*                  in the Pandoc manual into an AST and returns both
*                  that and the column alignments.
*                  Cf. https://pandoc.org/MANUAL.html#tables
*
* END HEADER
*/

import type { ParsedTable, ColAlignment } from './types'

/**
 * Parses a "simple" table ("simple", because it's not that simple)
 * @param {String|Array} A markdown table, either as line array or string
 * @returns {Object} An object with properties ast and colAlignments
 */
export default function parseSimpleTable (markdownTable: string|string[]): ParsedTable {
  // Let's begin. First we need a line array. Make sure to strip potential
  // newlines from beginning and end, if applicable. Also perform some
  // initial sanity checks on the table.
  if (!Array.isArray(markdownTable)) {
    markdownTable = markdownTable.split('\n')
  }

  if (markdownTable.length === 0 || (markdownTable.length === 1 && markdownTable[0].trim() === '')) {
    throw new Error('MarkdownTable was empty!')
  }

  const ast: string[][] = []
  const colAlignments: ColAlignment[] = []
  const colSizes: number[] = [] // Yes, here we need the lengths of the columns
  const colPadding: number[] = [] // Also yes: We need to know the padding between cols.

  // First, we need to determine the headers and the column alignments. As the
  // columns can be separated by an arbitrary amount of spaces, we need to
  // gauge the length AND the indentation by looking either at the first line
  // OR at the second line, depending on which one consists solely of dashes
  // and spaces.
  let determineAlignWith = markdownTable[0]
  let determineLengthsWith = markdownTable[1]
  if (/^[- ]+$/.test(markdownTable[0])) {
    // We got a headless table, so we need to swap the lines.
    determineLengthsWith = markdownTable[0]
    determineAlignWith = markdownTable[1]

    // Bonus round: According to the manual (TM) a headless table needs to have
    // a solely dashed line at the bottom.
    if (!/^[- ]+$/.test(markdownTable[markdownTable.length - 1])) {
      throw new Error('Malformed Markdown Table: Did not find a finishing dashed line for the table.')
    }
  }

  // Additional sanity check: The table is not allowed to be padded.
  if (determineLengthsWith.startsWith(' ') || determineLengthsWith.endsWith(' ')) {
    throw new Error('The table is padded.')
  }

  // Now we first need the indent and column sizes
  for (const match of determineLengthsWith.matchAll(/-+| +/g)) {
    // This RegExp is able to extract single-character-substrings
    if (match[0].startsWith('-')) {
      colSizes.push(match[0].length)
    } else if (match[0].startsWith(' ')) {
      colPadding.push(match[0].length)
    }
  }

  // Now, determine the alignments. Do so by first splitting the respective
  // line according to the lengths and see where the whitespace is.
  let index = 0
  for (let i = 0; i < colSizes.length; i++) {
    colAlignments.push(determineAlignWith.substring(index, index + colSizes[i]) as any)
    index += colSizes[i] + colPadding[i]
  }

  // Transform the colAlignments containing real text into alignments
  for (let i = 0; i < colAlignments.length; i++) {
    let a = colAlignments[i]
    if (a.startsWith(' ') && a.endsWith(' ')) {
      colAlignments[i] = 'center'
    } else if (a.startsWith(' ')) {
      colAlignments[i] = 'right'
    } else {
      colAlignments[i] = 'left'
    }
  }

  // Now we should have column sizes and indentation sizes.
  // Next: Parse the full table according to these rules.
  for (const line of markdownTable) {
    if (/^[- ]+$/.test(line)) {
      continue // Heading row
    }

    const columns: string[] = []
    let index = 0
    for (let i = 0; i < colSizes.length; i++) {
      columns.push(line.substring(index, index + colSizes[i]).trim())
      index += colSizes[i] + colPadding[i]
    }
    ast.push(columns)
  }

  if (ast.length === 0 || ast[0].length === 0) {
    // This AST does not look as it's supposed to -> abort
    throw new Error('Malformed Markdown Table! The parsed AST was empty.')
  }

  // Return the AST
  return { ast, colAlignments }
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        PersistentDataContainer
 * CVM-Role:        Model
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     The persistent data container is a simple class that allows
 *                  one to continuously persist some data to disk. The container
 *                  will make sure to balance disk load and persistence.
 *
 * END HEADER
 */

import { promises as fs, constants as FSConstants } from 'fs'
import { parse as parseYAML, stringify as stringifyYAML } from 'yaml'

export default class PersistentDataContainer<T = any> {
  /**
   * The absolute path to the data file. Must include the file extension (e.g.,
   * yaml or json).
   *
   * @var {string}
   */
  private readonly _filePath: string
  /**
   * The datatype with which the container should store the data on disk, can be
   * either yaml or json.
   *
   * @var {'yaml'|'json'}
   */
  private readonly _dataType: 'yaml'|'json'
  /**
   * The data of the container. Initially set to undefined, after initializing/
   * getting of the data, contains the most recent data. Will be overwritten by
   * calling set()
   *
   * @var {T}
   */
  private _data: T

  /**
   * Holds the most recent timeout for writing the data to disk if applicable
   *
   * @var {NodeJS.Timeout|undefined}
   */
  private _timeout: NodeJS.Timeout|undefined

  /**
   * The delay (in milliseconds) of wait before actually writing the data to
   * disk.
   *
   * @var {number}
   */
  private _delay: number

  /**
   * Creates a new persistent data container.
   *
   * @param   {string}         filePath  The absolute path to the data file
   * @param   {'yaml'|'json'}  type      The file format to use, defaults to JSON
   * @param   {number}         delay     The time to wait before writing, defaults to 1000ms
   *
   * @return  {PersistentDataContainer}  The file container
   */
  constructor (filePath: string, type: 'yaml'|'json' = 'json', delay: number = 1000) {
    this._filePath = filePath
    this._dataType = type
    this._delay = delay
  }

  /**
   * Initializes the container with some data. Must be called before get()
   *
   * @param   {T}      initialData  The initial data to write into the file
   */
  public async init (initialData: T): Promise<void> {
    if (initialData === undefined || initialData === null) {
      throw new Error('Cannot initialize data storage with null or undefined!')
    }

    this._data = initialData
    await fs.writeFile(this._filePath, this.stringify(), { encoding: 'utf-8' })
  }

  /**
   * Checks if the data store has already been initialized. If this function
   * returns false, you need to call init() prior.
   *
   * @return  {Promise<boolean>}  Returns whether the store has been initialized
   */
  public async isInitialized (): Promise<boolean> {
    try {
      await fs.access(this._filePath, FSConstants.R_OK | FSConstants.W_OK)
      const contents = await fs.readFile(this._filePath, 'utf-8')
      return contents.trim() !== ''
    } catch (err: any) {
      return false
    }
  }

  /**
   * Overwrites the stored data and sets a timeout for writing the data to disk.
   * Writes to disk if no changes occurred after the set amount of delay. NOTE
   * that passing non-serializable objects such as functions will lead to data
   * loss or can throw errors.
   *
   * @param   {T}   newData  The new data
   */
  public set (newData: T): void {
    if (newData === undefined || newData === null) {
      throw new Error('Cannot set the data to "undefined" or "null"!')
    }

    // Clone the data so it becomes detached from the caller. This also ensures
    // that this function already throws an error if the data cannot be serialized.
    this._data = JSON.parse(JSON.stringify(newData))

    if (this._timeout !== undefined) {
      clearTimeout(this._timeout)
    }

    this._timeout = setTimeout(() => { this.flushToDisk() }, this._delay)
  }

  /**
   * Reads the data file and returns the data
   *
   * @return  {Promise<Partial<T>>}  Resolves with the data contained in the file. NOTE that the returned type is a partial T to indicate that the container does not perform a sanity check on the data
   */
  public async get (): Promise<Partial<T>> {
    if (this._timeout !== undefined) {
      // _data contains the most recent iteration, NOT the datafile. So return
      // that one instead.
      return this._data
    }

    try {
      const content = await fs.readFile(this._filePath, { encoding: 'utf-8' })

      if (this._dataType === 'json') {
        this._data = JSON.parse(content)
      } else {
        this._data = parseYAML(content)
      }
    } catch (err: any) {
      throw new Error('Could not retrieve container contents: Either the contents were malformed, or you forgot to init the container. ' + String(err.message))
    }

    return this._data
  }

  /**
   * Changes the delay of waiting time before writing (in milliseconds)
   *
   * @param   {number}  delay  The new delay in ms
   */
  public setDelay (delay: number): void {
    this._delay = delay
  }

  /**
   * Flushes the content to disk if it has been modified in the meantime
   */
  private flushToDisk (): void {
    if (this._data === undefined) {
      return // No need to flush the data
    }

    if (this._timeout !== undefined) {
      clearTimeout(this._timeout)
      this._timeout = undefined
    }

    // TODO: Proper logging
    fs.writeFile(this._filePath, this.stringify(), { encoding: 'utf-8' })
      .catch(err => { console.error(err) })
  }

  /**
   * Stringifies the data to be written to disk according to the data type
   *
   * @return  {string}  The contained data as a serialized string
   */
  private stringify (): string {
    if (this._dataType === 'json') {
      // By passing a space as the third character, we make the JSON readable
      return JSON.stringify(this._data, undefined, '  ')
    } else {
      return stringifyYAML(this._data)
    }
  }

  /**
   * This shuts down the container. Can be used to speed up the shutdown of the
   * node process since this will clear the timeout and immediately write the
   * data to disk.
   */
  public shutdown (): void {
    if (this._timeout !== undefined) {
      this.flushToDisk() // One last flush to disk to prevent data loss
    }
  }
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        Replace Links
 * CVM-Role:        Utility
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     This function can replace a set of links across a file
 *
 * END HEADER
 */

import extractYamlFrontmatter from './extract-yaml-frontmatter'
import path from 'path'

/**
 * Takes a Markdown document and replaces all occurrences of a link to
 * oldContent with links to newContent. NOTE: Do not remove the file extensions,
 * the function will do that for you!
 *
 * @param   {string}        markdown    The document in question
 * @param   {string}        oldContent  The link to be replaced.
 * @param   {string}        newContent  The new link contents
 *
 * @return  {string}                    The new document
 */
export default function replaceLinks (markdown: string, oldContent: string, newContent: string): string {
  // Users can link both to `Zettelkasten.md` as well as to `Zettelkasten`.
  // The "withoutExtension" vars will equal the arguments to the function if
  // there is no extension given.
  const oldWithoutExtension = path.basename(oldContent, path.extname(oldContent))
  const newWithoutExtension = path.basename(newContent, path.extname(newContent))

  if (!markdown.includes(oldContent) && !markdown.includes(oldWithoutExtension)) {
    return markdown
  }

  // Finding (Wiki/Zettelkasten) links is easy ...
  const linkRE = /\[\[(.+?)\]\]/g
  // ... but we need to ensure we don't arbitrarily parse anything in a
  // potential frontmatter
  const { frontmatter, content } = extractYamlFrontmatter(markdown)
  if (frontmatter !== null) {
    linkRE.lastIndex = markdown.indexOf(content)
  }

  // After that precaution, it's a simple matter of going through the document
  return markdown.replace(linkRE, (_, content: string) => {
    if (content === oldContent) {
      return `[[${newContent}]]`
    } else if (content === oldWithoutExtension) {
      return `[[${newWithoutExtension}]]`
    } else {
      return `[[${content}]]`
    }
  })
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        Replace Tags
 * CVM-Role:        Utility
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     This function can replace a set of tags across a file
 *
 * END HEADER
 */

import { parseDocument, YAMLSeq } from 'yaml'
import extractYamlFrontmatter from './extract-yaml-frontmatter'
import { extractASTNodes, markdownToAST } from '@common/modules/markdown-utils'
import type { ZettelkastenTag } from '@common/modules/markdown-utils/markdown-ast'

/**
 * Takes a Markdown document and replaces all occurrences of oldTag with newTag.
 * Returns the new document after replacing.
 *
 * @param   {string}        markdown  The document in question
 * @param   {string}        oldTag    The old tag to be replaced
 * @param   {string}        newTag    The new tag to replace the old one
 *
 * @return  {string}                  The new document
 */
export default function replaceTags (markdown: string, oldTag: string, newTag: string): string {
  if (!markdown.includes(oldTag)) {
    return markdown
  }

  // Since tags can occur in a frontmatter as well as in the document text, we
  // have the following combinations of possibilities:
  // 1. If the oldTag contains spaces --> Can only be in the frontmatter
  // 2. No spaces at all --> Can occur anywhere and has to be replaced anywhere
  // 3. Only newTag has spaces --> Remove oldTag from text, add newTag to frontmatter
  const { frontmatter, content } = extractYamlFrontmatter(markdown)

  if (frontmatter !== null) {
    const stringFrontmatter = markdown.slice(0, markdown.indexOf(content))
    const ast = parseDocument(stringFrontmatter)
    // Now, find the keywords or tags-property
    const prop = ast.get('keywords', true) ?? ast.get('tags', true)

    if (prop !== undefined && prop instanceof YAMLSeq) {
      for (const item of prop.items) {
        if (item.value === oldTag) {
          const [ start, valueEnd ] = item.range
          // Slice the correct position
          markdown = markdown.slice(0, start) + newTag + markdown.slice(valueEnd)
        }
      }

      // If the old tag contained a space, we are already done
      if (/\s/.test(oldTag)) {
        return markdown
      }
    }
  }

  // Now, we can do a much simpler approach to replacing the tag in the rest of
  // the content with simple RegEx.
  const ast = markdownToAST(markdown)
  const tagNodes = extractASTNodes(ast, 'ZettelkastenTag') as ZettelkastenTag[]

  // Please NOTE that we are reversing the tagNodes array so that the positions
  // of the tags in the new document remain valid, even after replacing the tags
  const newTagHasSpaces = /\s/.test(newTag)
  for (const tagNode of tagNodes.reverse()) {
    if (tagNode.value !== oldTag) {
      continue
    }

    const before = markdown.slice(0, tagNode.from)
    const after = markdown.slice(tagNode.to)
    // If the new tag contains spaces, we have to remove the tag here
    markdown = before + (newTagHasSpaces ? '' : `#${newTag}`) + after
  }

  return markdown
}

/**
 * BEGIN HEADER
 *
 * Contains:        Utility function
 * CVM-Role:        <none>
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     This file contains a function that can be used to safely
 *                  assign object values, that is: you pass a reference
 *                  object, and some other object. What will be returned is a
 *                  new object that contains all fields from the reference, but
 *                  does not include any "invalid" fields that were only
 *                  present on the passed object, but not on the reference.
 *                  This also supports nested objects.
 *
 * END HEADER
 */

/**
 * Merges an object with a reference, creating a new object that will contain
 * all properties of `referenceObject`. The value of those properties will
 * either be taken from the object, if it possesses that property, or from the
 * `referenceObject` otherwise.
 *
 * @param  {Partial<A>}  obj              The object with property values to be
 *                                        merged.
 * @param  {A}           referenceObject  The reference to use the properties
 *                                        and default values from.
 * @param  {Partial<A>}  newObject        DO NOT USE -- INTERNAL! The new object
 *                                        to be returned.
 *
 * @return {A}                            The cloned object with properties of
 *                                        `referenceObject` with values of `obj`
 *                                        merged in.
 */
export default function safeAssign <A extends object> (obj: Partial<A>, referenceObject: A, newObject: Partial<A> = {}): A {
  // Iterate over all properties of referenceObject
  for (const prop in referenceObject) {
    if (prop in obj) {
      // The object has the property, so now we have to decide over two cases:
      // either it's a sub-object --> in this case we'll have to apply
      // recursively. We perform an explicit null-check, since
      // `typeof null === 'object'`.
      if (typeof referenceObject[prop] === 'object' &&
        !Array.isArray(referenceObject[prop]) &&
        referenceObject[prop] !== null) {
        // @ts-expect-error These properties will be filled in recursively
        newObject[prop] = {}
        // @ts-expect-error TypeScript wouldn't not treat this as an error.
        // (To be fair, we do violence to the type system here.)
        safeAssign(obj[prop], referenceObject[prop], newObject[prop])
      } else {
        // Assign the primitive
        newObject[prop] = obj[prop]
      }
    } else {
      // `obj` doesn't have prop, so take the value from reference instead.
      newObject[prop] = referenceObject[prop]
    }
  }

  // Type-cast, since now newObject is of type A
  return newObject as A
}

/**
 * @ignore
 * BEGIN HEADER
 *
 * Contains:        Visual URL shortener
 * CVM-Role:        Utility Function
 * Maintainer:      Hendrik Erz
 * License:         GNU GPL v3
 *
 * Description:     This function does its best to properly shorten the given
 *                  URL visually so that it fits within, e.g., a fixed-width
 *                  container
 *
 * END HEADER
 */

/**
 * Takes a long URL and shortens it as best as possible so that it can be
 * presented with as much information as possible to the user given the length
 * requirement (default: 80 characters). If url is shorter than that, the
 * function returns the url unchanged, if it's a valid URL, it will try various
 * heuristics to shorten the URL without hiding too much information on the
 * origin of the URL, and if it's not a valid URL, it will return a possibly
 * improperly truncated URL.
 *
 * @param  {string}  url        The URL to shorten
 * @param  {number}  maxLength  The maximum number of characters for the
 *                              shortened URL
 *
 * @return {string}             The URL in a way that fits maxLength
 */
export function shortenUrlVisually (url: string, maxLength: number = 80): string {
  // Plain links need to be surrounded by <>, so we can definitely remove those
  // if present.
  url = url.replace(/^<(.+)>$/, '$1')
  if (url.length <= maxLength) {
    return url
  }

  try {
    const parsedUrl = new URL(url)
    const {
      origin, // https://www.example.com
      hostname, // www.example.com
      pathname, // /some/subsite.php
      protocol // https:
    } = parsedUrl

    if (origin.length + pathname.length <= maxLength) {
      return origin + pathname
    } else if (hostname.length + pathname.length <= maxLength && /https?:/.test(protocol)) {
      // Only hostname works, and protocol is known
      return parsedUrl.hostname + pathname
    } else if (hostname.length < maxLength) {
      // Truncate path length
      const remaining = maxLength - hostname.length
      return hostname + '/' + pathname.slice(-remaining)
    } else {
      // We cannot really accommodate maxLength, so just do a brutal truncate
      return hostname.slice(0, maxLength)
    }
  } catch (err: any) {
    // Fallback: Not a valid URL
    const half = Math.floor(maxLength / 2)
    return url.slice(0, half) + '' + url.slice(-half)
  }
}
